import React, { useMemo, useEffect, useState, useCallback } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap, Circle, ZoomControl } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix Leaflet's default icon paths when using CRA / webpack
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png')
});

// Custom animated marker with price tag
const createPriceMarker = (price, isSelected = false) => {
  const formattedPrice = price >= 1000 
    ? `R${(price/1000).toFixed(price % 1000 === 0 ? 0 : 1)}k`
    : `R${price}`;
  
  return new L.DivIcon({
    className: 'custom-price-marker',
    html: `
      <div class="price-marker-container ${isSelected ? 'selected' : ''}">
        <div class="price-marker-bubble">
          <span class="price-text">${formattedPrice}</span>
        </div>
        <div class="price-marker-arrow"></div>
      </div>
    `,
    iconSize: [80, 45],
    iconAnchor: [40, 45],
    popupAnchor: [0, -45],
  });
};

// User location marker (pulsing dot)
const userLocationIcon = new L.DivIcon({
  className: 'user-location-marker',
  html: `
    <div class="user-location-container">
      <div class="user-location-pulse"></div>
      <div class="user-location-dot"></div>
    </div>
  `,
  iconSize: [24, 24],
  iconAnchor: [12, 12],
});

// Custom marker icons with distinct colors for different listing types
// eslint-disable-next-line no-unused-vars
const createListingIcon = (type = 'default', price) => {
  if (price) {
    return createPriceMarker(price);
  }
  
  const colors = {
    default: '#ef4444',
    premium: '#f59e0b',
    verified: '#10b981',
    new: '#0d9488'
  };
  
  const color = colors[type] || colors.default;
  
  return new L.Icon({
    iconUrl: 'data:image/svg+xml;base64,' + btoa(`
      <svg width="32" height="44" viewBox="0 0 32 44" xmlns="http://www.w3.org/2000/svg">
        <defs>
          <filter id="shadow" x="-20%" y="-20%" width="140%" height="140%">
            <feDropShadow dx="0" dy="2" stdDeviation="2" flood-color="#000" flood-opacity="0.3"/>
          </filter>
        </defs>
        <path d="M16 0C7.163 0 0 7.163 0 16c0 12 16 28 16 28s16-16 16-28C32 7.163 24.837 0 16 0z" fill="${color}" filter="url(#shadow)"/>
        <circle cx="16" cy="15" r="7" fill="white"/>
        <path d="M16 11l-4 3v5h3v-3h2v3h3v-5l-4-3z" fill="${color}"/>
      </svg>
    `),
    iconSize: [32, 44],
    iconAnchor: [16, 44],
    popupAnchor: [0, -44],
  });
};

// Mock location coordinates for South African areas
const locationCoordinates = {
  'soweto': [-26.2373, 27.8471],
  'johannesburg': [-26.2023, 28.0436],
  'pretoria': [-25.7461, 28.2333],
  'durban': [-29.8587, 31.0192],
  'cape town': [-33.9249, 18.4241],
  'sandton': [-26.1088, 28.0545],
  'midrand': [-25.9860, 28.1144],
  'benoni': [-26.1917, 28.3603],
  'boksburg': [-26.1972, 28.2328],
  'alberton': [-26.2745, 28.1114],
};

const getCoordinatesForLocation = (location) => {
  const normalized = location.toLowerCase().trim();
  for (const [key, coords] of Object.entries(locationCoordinates)) {
    if (normalized.includes(key)) {
      return coords;
    }
  }
  return [-26.2023, 28.0436]; // Default to Johannesburg
};

function FitBounds({ bounds }) {
  const map = useMap();
  useEffect(() => {
    if (bounds && bounds.length > 0) {
      const group = new L.FeatureGroup();
      bounds.forEach(coord => group.addLayer(L.marker(coord)));
      map.fitBounds(group.getBounds(), { padding: [60, 60], maxZoom: 15 });
    }
  }, [bounds, map]);
  return null;
}

// Map style selector component
function MapStyleSelector({ currentStyle, onStyleChange }) {
  const styles = [
    { id: 'streets', label: 'Streets', icon: 'üó∫Ô∏è' },
    { id: 'satellite', label: 'Satellite', icon: 'üõ∞Ô∏è' },
    { id: 'dark', label: 'Dark', icon: 'üåô' },
  ];

  return (
    <div className="map-style-selector">
      {styles.map(style => (
        <button
          key={style.id}
          onClick={() => onStyleChange(style.id)}
          className={`map-style-btn ${currentStyle === style.id ? 'active' : ''}`}
          title={style.label}
        >
          <span className="style-icon">{style.icon}</span>
          <span className="style-label">{style.label}</span>
        </button>
      ))}
    </div>
  );
}

function OverlayControls({ userLocation, nearbyRadius, onRecenter, onRadiusChange, mapStyle, onMapStyleChange, listingsCount }) {
  const map = useMap();
  const [isExpanded, setIsExpanded] = useState(false);

  return (
    <>
      {/* Top-left: Listings count badge */}
      <div className="map-listings-badge">
        <span className="badge-icon">üè†</span>
        <span className="badge-count">{listingsCount}</span>
        <span className="badge-label">rooms</span>
      </div>

      {/* Top-right: Controls */}
      <div className="map-controls-container">
        {/* Map style toggle */}
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className="map-control-btn map-style-toggle"
          title="Change map style"
        >
          <span>üó∫Ô∏è</span>
        </button>
        
        {isExpanded && (
          <MapStyleSelector 
            currentStyle={mapStyle} 
            onStyleChange={(style) => {
              onMapStyleChange(style);
              setIsExpanded(false);
            }} 
          />
        )}

        {/* Recenter button */}
        {userLocation && (
          <button
            type="button"
            onClick={() => { 
              if (onRecenter) onRecenter(); 
              map.setView([userLocation.lat, userLocation.lon], 14, { animate: true }); 
            }}
            className="map-control-btn recenter-btn"
            title="Center on my location"
          >
            <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <circle cx="12" cy="12" r="3"/>
              <path d="M12 2v4M12 18v4M2 12h4M18 12h4"/>
            </svg>
          </button>
        )}

        {/* Fullscreen toggle */}
        <button
          type="button"
          onClick={() => {
            const container = map.getContainer().parentElement;
            if (document.fullscreenElement) {
              document.exitFullscreen();
            } else {
              container?.requestFullscreen();
            }
          }}
          className="map-control-btn fullscreen-btn"
          title="Toggle fullscreen"
        >
          <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M8 3H5a2 2 0 0 0-2 2v3M21 8V5a2 2 0 0 0-2-2h-3M3 16v3a2 2 0 0 0 2 2h3M16 21h3a2 2 0 0 0 2-2v-3"/>
          </svg>
        </button>
      </div>

      {/* Bottom: Radius slider */}
      {userLocation && (
        <div className="map-radius-control">
          <div className="radius-header">
            <span className="radius-icon">üìç</span>
            <span className="radius-label">Search radius</span>
          </div>
          <div className="radius-slider-container">
            <input
              type="range"
              min={1}
              max={25}
              step={1}
              value={nearbyRadius}
              onChange={(e) => onRadiusChange && onRadiusChange(parseInt(e.target.value))}
              className="radius-slider"
            />
            <div className="radius-value">{nearbyRadius} km</div>
          </div>
          <div className="radius-presets">
            {[1, 5, 10, 25].map(r => (
              <button
                key={r}
                onClick={() => onRadiusChange && onRadiusChange(r)}
                className={`radius-preset-btn ${nearbyRadius === r ? 'active' : ''}`}
              >
                {r}km
              </button>
            ))}
          </div>
        </div>
      )}
    </>
  );
}

export default function MapView({ listings = [], onMarkerClick = () => {}, userLocation = null, nearbyRadius = 0, fullHeight = false, onRadiusChange, onRecenter }) {
  const [mapStyle, setMapStyle] = useState('streets');
  const [selectedMarkerId, setSelectedMarkerId] = useState(null);
  const [hoveredMarkerId, setHoveredMarkerId] = useState(null);

  const markers = useMemo(() => {
    return listings
      .map(l => {
        let lat = l.latitude ?? l.lat ?? (l.coords && l.coords[0]);
        let lng = l.longitude ?? l.lng ?? (l.coords && l.coords[1]);
        
        // Fallback to location-based coordinates if no explicit coords
        if (typeof lat !== 'number' || typeof lng !== 'number') {
          [lat, lng] = getCoordinatesForLocation(l.location);
        }
        
        return { id: l.id, title: l.title, location: l.location, price: l.price, lat, lng, raw: l, photos: l.photos };
      });
  }, [listings]);

  const defaultCenter = [-26.2041, 28.0473]; // Johannesburg fallback
  const bounds = markers.length > 0 ? markers.map(m => [m.lat, m.lng]) : null;

  // include user location in bounds so map can show renter + listings together
  const allBounds = useMemo(() => {
    const b = bounds ? [...bounds] : [];
    if (userLocation && typeof userLocation.lat === 'number' && typeof userLocation.lon === 'number') {
      b.push([userLocation.lat, userLocation.lon]);
    }
    return b.length > 0 ? b : null;
  }, [bounds, userLocation]);

  // Map tile layers based on style
  const getTileLayer = useCallback(() => {
    const MAPBOX_KEY = process.env.REACT_APP_MAPBOX_API_KEY;
    
    switch (mapStyle) {
      case 'satellite':
        if (MAPBOX_KEY) {
          return {
            url: `https://api.mapbox.com/styles/v1/mapbox/satellite-streets-v12/tiles/{z}/{x}/{y}?access_token=${MAPBOX_KEY}`,
            attribution: '¬© Mapbox ¬© OpenStreetMap'
          };
        }
        return {
          url: 'https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}',
          attribution: '¬© Esri'
        };
      case 'dark':
        if (MAPBOX_KEY) {
          return {
            url: `https://api.mapbox.com/styles/v1/mapbox/dark-v11/tiles/{z}/{x}/{y}?access_token=${MAPBOX_KEY}`,
            attribution: '¬© Mapbox'
          };
        }
        return {
          url: 'https://{s}.basemaps.cartocdn.com/dark_all/{z}/{x}/{y}{r}.png',
          attribution: '¬© CartoDB'
        };
      default: // streets
        if (MAPBOX_KEY) {
          return {
            url: `https://api.mapbox.com/styles/v1/mapbox/streets-v12/tiles/{z}/{x}/{y}?access_token=${MAPBOX_KEY}`,
            attribution: '¬© Mapbox'
          };
        }
        return {
          url: 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
          attribution: '¬© OpenStreetMap contributors'
        };
    }
  }, [mapStyle]);

  const tileLayer = getTileLayer();
  const mapHeight = fullHeight ? 'calc(100vh - 180px)' : '320px';

  const handleMarkerClick = useCallback((marker) => {
    setSelectedMarkerId(marker.id);
    onMarkerClick(marker.raw);
  }, [onMarkerClick]);

  return (
    <div className="map-container-wrapper" style={{ height: mapHeight }}>
      {markers.length === 0 ? (
        <div className="map-empty-state">
          <div className="empty-state-icon">üó∫Ô∏è</div>
          <h3 className="empty-state-title">No listings on the map</h3>
          <p className="empty-state-text">
            {listings.length === 0 
              ? "No listings available in this area" 
              : "Listings don't have location data yet"}
          </p>
          {listings.length > 0 && (
            <div className="empty-state-list">
              {listings.slice(0, 3).map(l => (
                <button 
                  key={l.id} 
                  onClick={() => onMarkerClick(l)} 
                  className="empty-state-listing"
                >
                  <span className="listing-title">{l.title}</span>
                  <span className="listing-price">R{Number(l.price).toLocaleString()}</span>
                </button>
              ))}
              {listings.length > 3 && (
                <p className="empty-state-more">+{listings.length - 3} more listings</p>
              )}
            </div>
          )}
        </div>
      ) : (
        <MapContainer 
          center={markers[0] ? [markers[0].lat, markers[0].lng] : defaultCenter} 
          zoom={12} 
          className="map-container"
          zoomControl={false}
        >
          <ZoomControl position="bottomright" />
          <TileLayer
            attribution={tileLayer.attribution}
            url={tileLayer.url}
          />
          
          <OverlayControls 
            userLocation={userLocation} 
            nearbyRadius={nearbyRadius} 
            onRecenter={onRecenter} 
            onRadiusChange={onRadiusChange}
            mapStyle={mapStyle}
            onMapStyleChange={setMapStyle}
            listingsCount={markers.length}
          />

          {/* User location marker with pulse effect */}
          {userLocation && typeof userLocation.lat === 'number' && typeof userLocation.lon === 'number' && (
            <>
              <Marker position={[userLocation.lat, userLocation.lon]} icon={userLocationIcon}>
                <Popup className="user-location-popup">
                  <div className="popup-user-location">
                    <div className="popup-icon">üìç</div>
                    <div className="popup-content">
                      <strong>Your Location</strong>
                      <span className="popup-accuracy">Approximate position</span>
                    </div>
                  </div>
                </Popup>
              </Marker>
              {nearbyRadius > 0 && (
                <Circle 
                  center={[userLocation.lat, userLocation.lon]} 
                  radius={nearbyRadius * 1000} 
                  pathOptions={{ 
                    color: '#0d9488', 
                    fillColor: '#0d9488',
                    fillOpacity: 0.08,
                    weight: 2,
                    dashArray: '5, 10'
                  }} 
                />
              )}
            </>
          )}

          {/* Listing markers with price tags */}
          {markers.map(m => (
            <Marker 
              key={m.id} 
              position={[m.lat, m.lng]} 
              icon={createPriceMarker(m.price, selectedMarkerId === m.id || hoveredMarkerId === m.id)}
              eventHandlers={{ 
                click: () => handleMarkerClick(m),
                mouseover: () => setHoveredMarkerId(m.id),
                mouseout: () => setHoveredMarkerId(null)
              }}
            >
              <Popup className="listing-popup">
                <div className="popup-listing-card">
                  {m.photos && m.photos[0] && (
                    <div className="popup-image">
                      <img src={m.photos[0]} alt={m.title} />
                    </div>
                  )}
                  <div className="popup-details">
                    <h4 className="popup-title">{m.title}</h4>
                    <p className="popup-location">
                      <span className="location-icon">üìç</span>
                      {m.location}
                    </p>
                    <div className="popup-footer">
                      <span className="popup-price">R{Number(m.price).toLocaleString()}<small>/mo</small></span>
                      <button 
                        className="popup-view-btn"
                        onClick={() => handleMarkerClick(m)}
                      >
                        View Details ‚Üí
                      </button>
                    </div>
                  </div>
                </div>
              </Popup>
            </Marker>
          ))}

          <FitBounds bounds={allBounds || bounds} />
        </MapContainer>
      )}
    </div>
  );
}
