import React, { useState } from 'react';
import { X, Search, MapPin, User, Phone, Mail, ArrowLeft, ShieldCheck, Star, Maximize } from 'lucide-react';
import VirtualTourViewer from './VirtualTourViewer';

function PhotoGallery({ photos, currentIndex, onClose, onNavigate }) {
  const [touchStart, setTouchStart] = useState(null);

  const handleTouchStart = (e) => {
    if (e.touches && e.touches.length === 1) {
      const t = e.touches[0];
      setTouchStart({ x: t.clientX, y: t.clientY, time: Date.now() });
    }
  };

  const handleTouchEnd = (e) => {
    if (!touchStart || !e.changedTouches || e.changedTouches.length === 0) return;
    const t = e.changedTouches[0];
    const dx = t.clientX - touchStart.x;
    const dy = t.clientY - touchStart.y;
    const dt = Date.now() - touchStart.time;
    const fast = dt < 600; // quick swipe threshold
    const horiz = Math.abs(dx) > 60 && Math.abs(dx) > Math.abs(dy);
    const vertDown = dy > 80 && Math.abs(dy) > Math.abs(dx);
    if (fast && horiz) {
      if (dx < 0) {
        onNavigate('next');
      } else {
        onNavigate('prev');
      }
    } else if (fast && vertDown) {
      onClose();
    }
    setTouchStart(null);
  };

  return (
    <div
      className="fixed inset-0 bg-black/95 z-50 flex flex-col"
      onTouchStart={handleTouchStart}
      onTouchEnd={handleTouchEnd}
    >
      {/* Header */}
      <div className="flex justify-between items-center p-4 text-white safe-area-top">
        <div className="flex items-center gap-3">
          <button 
            onClick={onClose} 
            className="p-2 hover:bg-white/10 rounded-full transition-colors"
            aria-label="Close gallery"
          >
            <X className="w-6 h-6" />
          </button>
          <span className="text-sm font-medium bg-white/10 px-3 py-1 rounded-full">
            {currentIndex + 1} of {photos.length}
          </span>
        </div>
      </div>
      
      {/* Main Image */}
      <div className="flex-1 flex items-center justify-center relative px-4">
        {currentIndex > 0 && (
          <button
            onClick={() => onNavigate('prev')}
            className="absolute left-2 sm:left-6 bg-white/20 backdrop-blur-sm text-white p-3 sm:p-4 rounded-full hover:bg-white/30 z-10 transition-all duration-200 hover:scale-110 active:scale-95 shadow-lg"
            aria-label="Previous photo"
          >
            <ArrowLeft className="w-5 h-5 sm:w-6 sm:h-6" />
          </button>
        )}
        <img 
          src={photos[currentIndex]} 
          alt={`Room view ${currentIndex + 1}`} 
          className="max-w-full max-h-full object-contain rounded-lg shadow-2xl transition-opacity duration-300" 
        />
        {currentIndex < photos.length - 1 && (
          <button
            onClick={() => onNavigate('next')}
            className="absolute right-2 sm:right-6 bg-white/20 backdrop-blur-sm text-white p-3 sm:p-4 rounded-full hover:bg-white/30 z-10 transition-all duration-200 hover:scale-110 active:scale-95 shadow-lg"
            aria-label="Next photo"
          >
            <ArrowLeft className="w-5 h-5 sm:w-6 sm:h-6 transform rotate-180" />
          </button>
        )}
        
        {/* Swipe hint */}
        <div className="absolute bottom-4 left-1/2 -translate-x-1/2 text-center text-xs text-white/60 bg-black/30 backdrop-blur-sm px-4 py-2 rounded-full hidden sm:block">
          Use arrow keys or swipe to navigate
        </div>
      </div>
      
      {/* Thumbnails */}
      <div className="p-4 safe-area-bottom">
        <div className="flex gap-2 overflow-x-auto pb-2 scrollbar-hide">
          {photos.map((photo, index) => (
            <button
              key={index}
              onClick={() => onNavigate('goto', index)}
              className={`flex-shrink-0 w-16 h-16 sm:w-20 sm:h-20 rounded-lg overflow-hidden transition-all duration-200 ${index === currentIndex ? 'ring-2 ring-teal-400 ring-offset-2 ring-offset-black scale-105' : 'opacity-50 hover:opacity-80'}`}
            >
              <img
                src={photo}
                alt={`Thumbnail ${index + 1}`}
                className="w-full h-full object-cover"
              />
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}

export default function ListingDetailModal({ listing, landlord, onClose }) {
  const [showContact, setShowContact] = useState(false);
  const [showGallery, setShowGallery] = useState(false);
  const [currentPhotoIndex, setCurrentPhotoIndex] = useState(0);
  const [showReport, setShowReport] = useState(false);
  const [reportReason, setReportReason] = useState('');
  const [reportComment, setReportComment] = useState('');
  const [reportStatus, setReportStatus] = useState('idle');
  const [showVirtualTour, setShowVirtualTour] = useState(false);
  // Reviews state
  const listingKey = listing?.id || `${listing.title}-${listing.createdAt || 'na'}`;
  const reviewsStorageKey = `reviews-${listingKey}`;
  const [reviews, setReviews] = useState(() => {
    try {
      const raw = localStorage.getItem(reviewsStorageKey);
      return raw ? JSON.parse(raw) : [];
    } catch {
      return [];
    }
  });
  const [newRating, setNewRating] = useState(0);
  const [hoverRating, setHoverRating] = useState(0);
  const [newComment, setNewComment] = useState('');
  const [submitStatus, setSubmitStatus] = useState('idle');

  const handleNavigate = (direction, index) => {
    if (direction === 'next' && currentPhotoIndex < listing.photos.length - 1) {
      setCurrentPhotoIndex(currentPhotoIndex + 1);
    } else if (direction === 'prev' && currentPhotoIndex > 0) {
      setCurrentPhotoIndex(currentPhotoIndex - 1);
    } else if (direction === 'goto') {
      setCurrentPhotoIndex(index);
    }
  };

  const reportReasons = [
    'Fraud / Scam',
    'Incorrect Information',
    'Safety Concern',
    'Inappropriate Content',
    'Other'
  ];

  const handleSubmitReport = (e) => {
    e.preventDefault();
    if (!reportReason) return;
    try {
      const key = listing.id || `${listing.title}-${listing.createdAt || Date.now()}`;
      const raw = localStorage.getItem('reports');
      const reports = raw ? JSON.parse(raw) : {};
      if (!reports[key]) reports[key] = [];
      reports[key].push({
        reason: reportReason,
        comment: reportComment.trim() || null,
        timestamp: Date.now()
      });
      localStorage.setItem('reports', JSON.stringify(reports));
      setReportStatus('submitted');
      setTimeout(() => {
        setShowReport(false);
        setReportReason('');
        setReportComment('');
        setReportStatus('idle');
      }, 1200);
    } catch (err) {
      setReportStatus('error');
    }
  };

  // Reviews helpers
  const avgRating = reviews.length
    ? Math.round((reviews.reduce((sum, r) => sum + (r.rating || 0), 0) / reviews.length) * 10) / 10
    : 0;

  const handleSubmitReview = (e) => {
    e.preventDefault();
    if (!newRating || newRating < 1) return;
    try {
      const entry = {
        rating: newRating,
        comment: newComment.trim(),
        timestamp: Date.now(),
      };
      const next = [...reviews, entry];
      setReviews(next);
      localStorage.setItem(reviewsStorageKey, JSON.stringify(next));
      setSubmitStatus('submitted');
      setNewRating(0);
      setNewComment('');
      setTimeout(() => setSubmitStatus('idle'), 1200);
    } catch {
      setSubmitStatus('error');
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-start sm:items-center justify-center p-0 sm:p-4 z-50 overflow-y-auto" aria-modal="true" role="dialog">
      <div className="bg-white rounded-none sm:rounded-lg max-w-2xl w-full h-full sm:h-auto sm:max-h-[90vh] overflow-y-auto shadow-lg">
        <div className="sticky top-0 bg-white border-b p-4 flex justify-between items-center z-10">
          <div className="flex items-center gap-3">
            <button onClick={onClose} className="text-gray-500 hover:text-gray-700 sm:hidden" aria-label="Back">
              <ArrowLeft className="w-6 h-6" />
            </button>
            <h2 className="text-lg md:text-xl font-bold text-gray-800">Room Details</h2>
          </div>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700 hidden sm:block" aria-label="Close">
            <X className="w-6 h-6" />
          </button>
        </div>
        <div className="p-4">
          {listing.photos && listing.photos.length > 0 && (
            <div className="relative mb-4 cursor-pointer" onClick={() => setShowGallery(true)}>
              <img src={listing.photos[currentPhotoIndex]} alt="Room" className="w-full h-64 object-cover rounded-lg" />
              {listing.photos.length > 1 && (
                <div className="absolute bottom-3 right-3 bg-black bg-opacity-60 text-white px-3 py-1 rounded-full text-xs md:text-sm flex items-center gap-1">
                  <Search className="w-4 h-4" />
                  View all {listing.photos.length} photos
                </div>
              )}
              {listing.photos.length > 1 && (
                <div className="absolute bottom-3 left-3 flex gap-1">
                  {listing.photos.map((_, index) => (
                    <div key={index} className={`w-2 h-2 rounded-full ${index === currentPhotoIndex ? 'bg-white' : 'bg-white bg-opacity-50'}`} />
                  ))}
                </div>
              )}
              {listing.photos.length >= 3 && (
                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    setShowVirtualTour(true);
                  }}
                  className="absolute top-3 right-3 bg-teal-600 hover:bg-teal-700 text-white px-3 py-2 rounded-lg text-sm font-semibold flex items-center gap-2 shadow-lg transition"
                >
                  <Maximize className="w-4 h-4" />
                  Virtual Tour
                </button>
              )}
            </div>
          )}
          {showGallery && listing.photos && (
            <PhotoGallery
              photos={listing.photos}
              currentIndex={currentPhotoIndex}
              onClose={() => setShowGallery(false)}
              onNavigate={handleNavigate}
            />
          )}
          {showVirtualTour && listing.photos && (
            <VirtualTourViewer
              photos={listing.photos}
              initialIndex={currentPhotoIndex}
              onClose={() => setShowVirtualTour(false)}
            />
          )}
          <h3 className="text-2xl font-bold mb-2 text-gray-800">{listing.title}</h3>
          <div className="text-teal-600 font-bold text-2xl mb-4">R{listing.price}/month</div>
          <div className="flex items-center text-gray-600 mb-4">
            <MapPin className="w-5 h-5 mr-2" />
            <span className="text-lg">{listing.location}</span>
          </div>
          {/* Rating summary */}
          <div className="flex items-center gap-2 mb-4">
            <div className="flex items-center">
              {[0,1,2,3,4].map(i => (
                <Star key={i} className={`w-5 h-5 ${avgRating > i ? 'text-yellow-500 fill-yellow-500' : 'text-gray-300'}`} />
              ))}
            </div>
            <span className="text-sm text-gray-700">
              {reviews.length > 0 ? `${avgRating} â€¢ ${reviews.length} review${reviews.length>1?'s':''}` : 'No reviews yet'}
            </span>
          </div>
          {listing.paymentMethod && (
            <div className="bg-green-50 border border-green-200 rounded-lg p-3 mb-4">
              <div className="flex items-center gap-2">
                <span className="text-sm font-semibold text-gray-700">Payment Method:</span>
                <span className="text-sm text-green-700 font-medium">{listing.paymentMethod}</span>
              </div>
            </div>
          )}
          <div className="bg-gray-50 rounded-lg p-4 mb-4">
            <h4 className="font-semibold mb-2 text-gray-700">Description</h4>
            <p className="text-gray-600">{listing.description || 'No description provided.'}</p>
          </div>
          {/* Reviews list */}
          <div className="bg-gray-50 rounded-lg p-4 mb-4">
            <h4 className="font-semibold mb-3 text-gray-700">Reviews</h4>
            {reviews.length === 0 && (
              <p className="text-sm text-gray-500">No reviews yet. Be the first to leave one.</p>
            )}
            {reviews.length > 0 && (
              <div className="space-y-3">
                {reviews.slice().reverse().map((r, idx) => (
                  <div key={idx} className="border border-gray-200 rounded-md p-3">
                    <div className="flex items-center gap-1 mb-1">
                      {[0,1,2,3,4].map(i => (
                        <Star key={i} className={`w-4 h-4 ${r.rating > i ? 'text-yellow-500 fill-yellow-500' : 'text-gray-300'}`} />
                      ))}
                      <span className="ml-2 text-xs text-gray-500">
                        {new Date(r.timestamp).toLocaleDateString()}
                      </span>
                    </div>
                    {r.comment && <p className="text-sm text-gray-700">{r.comment}</p>}
                  </div>
                ))}
              </div>
            )}
          </div>
          {/* Add a review */}
          <div className="bg-white rounded-lg p-4 mb-4 border border-gray-200">
            <h4 className="font-semibold mb-2 text-gray-700">Leave a Review</h4>
            <form onSubmit={handleSubmitReview} className="space-y-3">
              <div className="flex items-center gap-2">
                {[1,2,3,4,5].map(val => (
                  <button
                    key={val}
                    type="button"
                    onMouseEnter={() => setHoverRating(val)}
                    onMouseLeave={() => setHoverRating(0)}
                    onClick={() => setNewRating(val)}
                    className="focus:outline-none"
                    aria-label={`Rate ${val} star${val>1?'s':''}`}
                  >
                    <Star className={`w-6 h-6 ${((hoverRating || newRating) >= val) ? 'text-yellow-500 fill-yellow-500' : 'text-gray-300'}`} />
                  </button>
                ))}
                <span className="text-xs text-gray-600">{newRating ? `${newRating}/5` : 'Select rating'}</span>
              </div>
              <div>
                <textarea
                  value={newComment}
                  onChange={(e) => setNewComment(e.target.value)}
                  placeholder="Share details about your experience (optional)"
                  className="w-full border border-gray-300 rounded-md p-2 text-sm focus:outline-none focus:ring-2 focus:ring-teal-500"
                  rows={3}
                />
              </div>
              <div className="flex items-center justify-end gap-2">
                <button
                  type="submit"
                  disabled={!newRating || submitStatus === 'submitted'}
                  className="text-sm px-4 py-2 rounded-md bg-teal-600 hover:bg-teal-700 text-white font-semibold disabled:opacity-50"
                >
                  {submitStatus === 'submitted' ? 'Saved' : 'Submit Review'}
                </button>
              </div>
              {submitStatus === 'error' && (
                <p className="text-xs text-red-600">Failed to save review. Try again.</p>
              )}
              {submitStatus === 'submitted' && (
                <p className="text-xs text-green-600">Review saved locally.</p>
              )}
            </form>
          </div>
          {landlord && (
            <div className="bg-teal-50 rounded-lg p-4 mb-4">
              <div className="flex items-center justify-between mb-3">
                <h4 className="font-semibold text-gray-700">Landlord Information</h4>
                <button
                  onClick={() => setShowContact(!showContact)}
                  className="text-teal-700 hover:text-teal-800 text-sm font-medium"
                >
                  {showContact ? 'Hide' : 'Show'} Contact
                </button>
              </div>
              <div className="flex items-center mb-3">
                {landlord.photo ? (
                  <img src={landlord.photo} alt={landlord.name} className="w-12 h-12 rounded-full object-cover mr-3" />
                ) : (
                  <div className="w-12 h-12 rounded-full bg-gray-300 flex items-center justify-center mr-3">
                    <User className="w-6 h-6 text-gray-600" />
                  </div>
                )}
                <div>
                  <p className="font-semibold text-gray-800 flex items-center gap-2">
                    {landlord.name}
                    {(landlord.verified || landlord.idNumber) && (
                      <span className="inline-flex items-center gap-1 px-2 py-0.5 rounded-full bg-blue-600 text-white text-[10px] font-semibold">
                        <ShieldCheck className="w-3 h-3" /> Verified
                      </span>
                    )}
                  </p>
                  <p className="text-xs text-gray-500">Property Owner</p>
                </div>
              </div>
              {showContact && (
                <div className="space-y-2 pt-3 border-t border-teal-100">
                  <div className="flex items-center text-sm">
                    <Phone className="w-4 h-4 mr-2 text-teal-700" />
                    <a href={`tel:${landlord.phone}`} className="text-teal-700 hover:underline">{landlord.phone}</a>
                  </div>
                  <div className="flex items-center text-sm">
                    <Mail className="w-4 h-4 mr-2 text-teal-700" />
                    <a href={`mailto:${landlord.email}`} className="text-teal-700 hover:underline">{landlord.email}</a>
                  </div>
                  {landlord.phone && (
                    <div className="flex items-center text-sm">
                      <img src="https://static.whatsapp.net/rsrc.php/v3/yP/r/iUeWW5IXfRb.png" alt="WhatsApp" className="w-4 h-4 mr-2" />
                      <a
                        href={`https://wa.me/${landlord.phone.replace(/[^0-9]/g,'')}`}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="text-teal-700 hover:underline"
                      >
                        WhatsApp Chat
                      </a>
                    </div>
                  )}
                </div>
              )}
              <div className="mt-4 text-right">
                <button
                  onClick={() => setShowReport(true)}
                  className="text-xs font-medium text-red-600 hover:text-red-700 underline"
                >
                  Report Listing
                </button>
              </div>
            </div>
          )}
          {showReport && (
            <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
              <div className="bg-white w-full max-w-md mx-4 rounded-lg shadow-lg p-5 relative">
                <button
                  onClick={() => setShowReport(false)}
                  className="absolute top-3 right-3 text-gray-500 hover:text-gray-700"
                  aria-label="Close report form"
                >
                  <X className="w-5 h-5" />
                </button>
                <h4 className="text-lg font-semibold mb-3 text-gray-800">Report Listing</h4>
                <form onSubmit={handleSubmitReport} className="space-y-4">
                  <div className="space-y-2">
                    {reportReasons.map(r => (
                      <label key={r} className="flex items-center gap-2 text-sm text-gray-700">
                        <input
                          type="radio"
                          name="reportReason"
                          value={r}
                          checked={reportReason === r}
                          onChange={() => setReportReason(r)}
                          className="text-teal-600 focus:ring-teal-500"
                        />
                        {r}
                      </label>
                    ))}
                  </div>
                  <div>
                    <textarea
                      value={reportComment}
                      onChange={(e) => setReportComment(e.target.value)}
                      placeholder="Optional details..."
                      className="w-full border border-gray-300 rounded-md p-2 text-sm focus:outline-none focus:ring-2 focus:ring-teal-500"
                      rows={3}
                    />
                  </div>
                  <div className="flex items-center justify-end gap-2">
                    <button
                      type="button"
                      onClick={() => setShowReport(false)}
                      className="text-sm px-3 py-2 rounded-md border border-gray-300 text-gray-700 hover:bg-gray-100"
                    >
                      Cancel
                    </button>
                    <button
                      type="submit"
                      disabled={!reportReason || reportStatus === 'submitted'}
                      className="text-sm px-4 py-2 rounded-md bg-red-600 hover:bg-red-700 text-white font-semibold disabled:opacity-50"
                    >
                      {reportStatus === 'submitted' ? 'Submitted' : 'Submit Report'}
                    </button>
                  </div>
                  {reportStatus === 'error' && (
                    <p className="text-xs text-red-600">Failed to save report. Try again.</p>
                  )}
                  {reportStatus === 'submitted' && (
                    <p className="text-xs text-green-600">Report received. Thank you.</p>
                  )}
                </form>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
