function ListingDetailModal({ listing, landlord, onClose, onSendMessage, userType, currentUser, onRequireAuth, previewMode }) {
  const [message, setMessage] = useState('');
  const [showContact, setShowContact] = useState(false);
  const [showGallery, setShowGallery] = useState(false);
  const [currentPhotoIndex, setCurrentPhotoIndex] = useState(0);
  const effectiveRole = previewMode ? 'renter' : userType;

  const handleNavigate = (direction, index) => {
    if (direction === 'next' && currentPhotoIndex < listing.photos.length - 1) {
      setCurrentPhotoIndex(currentPhotoIndex + 1);
    } else if (direction === 'prev' && currentPhotoIndex > 0) {
      setCurrentPhotoIndex(currentPhotoIndex - 1);
    } else if (direction === 'goto') {
      setCurrentPhotoIndex(index);
    }
  };

  const handleSend = () => {
    if (previewMode) {
      return; // read-only in preview
    }
    if (message.trim() && effectiveRole === 'renter') {
      onSendMessage(listing.id, message);
      setMessage('');
      showToast('Message sent!', 'success');
      onClose();
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        <div className="sticky top-0 bg-white border-b p-4 flex justify-between items-center">
          <h2 className="text-xl font-bold text-gray-800">Room Details</h2>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
            <X className="w-6 h-6" />
          </button>
        </div>

        <div className="p-4">
{listing.photos && listing.photos.length > 0 && (
  <div className="relative mb-4 cursor-pointer" onClick={() => setShowGallery(true)}>
    <img src={listing.photos[currentPhotoIndex]} alt="Room" className="w-full h-64 object-cover rounded-lg" />
    {listing.photos.length > 1 && (
      <div className="absolute bottom-3 right-3 bg-black bg-opacity-60 text-white px-3 py-1 rounded-full text-sm flex items-center gap-1">
        <Search className="w-4 h-4" />
        View all {listing.photos.length} photos
      </div>
    )}
    {listing.photos.length > 1 && (
      <div className="absolute bottom-3 left-3 flex gap-1">
        {listing.photos.map((_, index) => (
          <div
            key={index}
            className={`w-2 h-2 rounded-full ${
              index === currentPhotoIndex ? 'bg-white' : 'bg-white bg-opacity-50'
            }`}
          />
        ))}
      </div>
    )}
  </div>
)}

{showGallery && listing.photos && (
  <PhotoGallery
    photos={listing.photos}
    currentIndex={currentPhotoIndex}
    onClose={() => setShowGallery(false)}
    onNavigate={handleNavigate}
  />
)}

          <h3 className="text-2xl font-bold mb-2 text-gray-800">{listing.title}</h3>
          <div className="text-blue-600 font-bold text-2xl mb-4">R{listing.price}/month</div>

          <div className="flex items-center text-gray-600 mb-4">
            <MapPin className="w-5 h-5 mr-2" />
            <span className="text-lg">{listing.location}</span>
          </div>

          <div className="bg-gray-50 rounded-lg p-4 mb-4">
            <h4 className="font-semibold mb-2 text-gray-700">Description</h4>
            <p className="text-gray-600">{listing.description || 'No description provided.'}</p>
          </div>

          {effectiveRole === 'renter' && landlord && (
            <div className="bg-blue-50 rounded-lg p-4 mb-4">
              <div className="flex items-center justify-between mb-3">
                <h4 className="font-semibold text-gray-700">Landlord Information</h4>
                {currentUser ? (
                  <button
                    onClick={() => setShowContact(!showContact)}
                    className="text-blue-600 hover:text-blue-700 text-sm font-medium"
                  >
                    {showContact ? 'Hide' : 'Show'} Contact
                  </button>
                ) : (
                  <button
                    onClick={() => onRequireAuth && onRequireAuth('renter')}
                    className="text-blue-600 hover:text-blue-700 text-sm font-medium"
                  >
                    Sign in to view contact
                  </button>
                )}
              </div>

              <div className="flex items-center mb-3">
                {landlord.photo ? (
                  <img src={landlord.photo} alt={landlord.name} className="w-12 h-12 rounded-full object-cover mr-3" />
                ) : (
                  <div className="w-12 h-12 rounded-full bg-gray-300 flex items-center justify-center mr-3">
                    <User className="w-6 h-6 text-gray-600" />
                  </div>
                )}
                <div>
                  <p className="font-semibold text-gray-800">{landlord.name}</p>
                  <p className="text-xs text-gray-500">Property Owner</p>
                </div>
              </div>

              {currentUser && showContact && (
                <div className="space-y-2 pt-3 border-t border-blue-100">
                  <div className="flex items-center text-sm">
                    <Phone className="w-4 h-4 mr-2 text-blue-600" />
                    <a href={`tel:${landlord.phone}`} className="text-blue-600 hover:underline">
                      {landlord.phone}
                    </a>
                  </div>
                  <div className="flex items-center text-sm">
                    <Mail className="w-4 h-4 mr-2 text-blue-600" />
                    <a href={`mailto:${landlord.email}`} className="text-blue-600 hover:underline">
                      {landlord.email}
                    </a>
                  </div>
                </div>
              )}
            </div>
          )}

          {effectiveRole === 'renter' && (
            <div className="border-t pt-4">
              <h4 className="font-semibold mb-3 text-gray-700">Send a Message</h4>
              {currentUser ? (
                <ListingInlineMessageBox landlord={landlord} listing={listing} onSend={handleSend} previewMode={previewMode} />
              ) : (
                <div className="flex">
                  <button
                    onClick={() => onRequireAuth && onRequireAuth('renter')}
                    className="w-full bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded-lg transition"
                  >
                    Sign in to message
                  </button>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

// Inline message box component with validation for listing detail view
function ListingInlineMessageBox({ landlord, listing, onSend, previewMode }) {
  const [text, setText] = useState('');
  const [error, setError] = useState('');

  const validate = (val) => {
    if (!val.trim()) {
      setError('Message cannot be empty');
      return false;
    }
    if (val.length > 500) {
      setError('Message too long (max 500 characters)');
      return false;
    }
    setError('');
    return true;
  };

  const handleSend = () => {
    if (previewMode) return;
    if (validate(text)) {
      onSend(listing.id, text.trim());
      setText('');
      setError('');
    }
  };

  return (
    <div className="flex flex-col gap-2" aria-label="Send a message to landlord">
      <div className="flex gap-2">
        <input
          type="text"
          value={text}
          onChange={(e) => { setText(e.target.value); validate(e.target.value); }}
          onKeyDown={(e) => { if (!previewMode && e.key === 'Enter') { e.preventDefault(); handleSend(); } }}
          placeholder={`Message ${landlord ? landlord.name : 'landlord'}...`}
          aria-invalid={error ? 'true' : 'false'}
          aria-describedby={error ? 'inline-message-error' : undefined}
          maxLength={500}
          disabled={previewMode}
          className={`flex-1 px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent ${previewMode ? 'bg-gray-100 cursor-not-allowed text-gray-500' : ''} ${error ? 'border-red-500' : 'border-gray-300'}`}
        />
        <button
          onClick={handleSend}
          disabled={previewMode || !!error || !text.trim()}
          className={`px-6 py-2 rounded-lg transition flex items-center justify-center ${previewMode ? 'bg-gray-200 text-gray-400 cursor-not-allowed' : (!error && text.trim()) ? 'bg-blue-600 hover:bg-blue-700 text-white' : 'bg-gray-200 text-gray-500 cursor-not-allowed'}`}
          aria-label="Send message"
        >
          <Send className="w-5 h-5" />
        </button>
      </div>
      {error && <p id="inline-message-error" className="text-xs text-red-600" aria-live="polite">{error}</p>}
      {previewMode && <p className="text-xs text-indigo-600" aria-live="polite">Preview mode: messaging disabled.</p>}
    </div>
  );
}

function ConversationModal({ conversation, listing, onClose, onSendMessage, previewMode }) {
  const [message, setMessage] = useState('');
  const [messageError, setMessageError] = useState('');

  const validateMessage = (val) => {
    if (!val.trim()) {
      setMessageError('Message cannot be empty');
      return false;
    }
    if (val.length > 500) {
      setMessageError('Message too long (max 500 characters)');
      return false;
    }
    setMessageError('');
    return true;
  };

  const handleSend = () => {
    if (previewMode) return;
    if (validateMessage(message)) {
      onSendMessage(listing.id, message.trim());
      setMessage('');
      setMessageError('');
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] flex flex-col">
        <div className="bg-white border-b p-4 flex items-center">
          <button onClick={onClose} className="mr-3 text-gray-500 hover:text-gray-700" aria-label="Close conversation">
            <ArrowLeft className="w-6 h-6" />
          </button>
          <div>
            <h2 className="font-bold text-gray-800">{listing?.title}</h2>
            <p className="text-sm text-gray-600">{listing?.location}</p>
          </div>
        </div>

        <div className="flex-1 overflow-y-auto p-4 space-y-3">
          {conversation.messages.map(msg => (
            <div
              key={msg.id}
              className={`flex ${msg.sender === 'landlord' ? 'justify-start' : 'justify-end'}`}
            >
              <div
                className={`max-w-xs px-4 py-2 rounded-lg ${
                  msg.sender === 'landlord'
                    ? 'bg-gray-200 text-gray-800'
                    : 'bg-blue-600 text-white'
                }`}
              >
                <p>{msg.text}</p>
                <span className="text-xs opacity-75">
                  {new Date(msg.timestamp).toLocaleTimeString()}
                </span>
              </div>
            </div>
          ))}
        </div>

        <div className="border-t p-4 flex flex-col gap-2">
          <div className="flex gap-2">
            <input
              type="text"
              value={message}
              onChange={(e) => { setMessage(e.target.value); validateMessage(e.target.value); }}
              onKeyDown={(e) => { if (e.key === 'Enter') { e.preventDefault(); handleSend(); } }}
              placeholder="Type a message..."
              aria-invalid={messageError ? 'true' : 'false'}
              aria-describedby={messageError ? 'conversation-message-error' : undefined}
              maxLength={500}
              disabled={previewMode}
              className={`flex-1 px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent ${previewMode ? 'bg-gray-100 cursor-not-allowed text-gray-500' : ''} ${messageError ? 'border-red-500' : 'border-gray-300'}`}
            />
            <button
              onClick={handleSend}
              disabled={previewMode || !!messageError || !message.trim()}
              className={`px-6 py-2 rounded-lg transition flex items-center justify-center ${previewMode ? 'bg-gray-200 text-gray-400 cursor-not-allowed' : (!messageError && message.trim()) ? 'bg-blue-600 hover:bg-blue-700 text-white' : 'bg-gray-200 text-gray-500 cursor-not-allowed'}`}
              aria-label="Send message"
            >
              <Send className="w-5 h-5" />
            </button>
          </div>
          {messageError && <p id="conversation-message-error" className="text-xs text-red-600" aria-live="polite">{messageError}</p>}
          {previewMode && <p className="text-xs text-indigo-600" aria-live="polite">Preview mode: messaging disabled.</p>}
        </div>
      </div>
    </div>
  );
}

function AuthModal({ defaultType = 'renter', onClose, onSubmit }) {
  const [form, setForm] = useState({ name: '', phone: '', email: '', type: defaultType, photo: '' });

  const handleSubmit = () => {
    if (!form.name || !form.email) {
      alert('Please add a name and email');
      return;
    }
    onSubmit(form);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg w-full max-w-md p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-bold">Sign in / Create account</h3>
          <button onClick={onClose} className="text-gray-400 hover:text-gray-600"><X className="w-5 h-5" /></button>
        </div>

        <div className="space-y-3">
          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-1">Full name</label>
            <input type="text" value={form.name} onChange={(e) => setForm({ ...form, name: e.target.value })} className="w-full px-3 py-2 border rounded" />
          </div>

          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-1">Email</label>
            <input type="email" value={form.email} onChange={(e) => setForm({ ...form, email: e.target.value })} className="w-full px-3 py-2 border rounded" />
          </div>

          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-1">Phone (optional)</label>
            <input type="tel" value={form.phone} onChange={(e) => setForm({ ...form, phone: e.target.value })} className="w-full px-3 py-2 border rounded" />
          </div>

          <div>
            <label className="block text-sm font-semibold text-gray-700 mb-1">I am a</label>
            <select value={form.type} onChange={(e) => setForm({ ...form, type: e.target.value })} className="w-full px-3 py-2 border rounded">
              <option value="renter">Looking for a room</option>
              <option value="landlord">Listing rooms</option>
            </select>
          </div>

          <div className="flex gap-3 pt-4">
            <button onClick={onClose} className="flex-1 bg-gray-100 hover:bg-gray-200 py-2 rounded">Cancel</button>
            <button onClick={handleSubmit} className="flex-1 bg-blue-600 hover:bg-blue-700 text-white py-2 rounded">Continue</button>
          </div>
        </div>
      </div>
    </div>
  );
}

