                  <button onClick={() => openAuthModal('landlord')} className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-lg">List Your Room</button>
                </div>
              )}

              {currentUser && (
                <>
                  <div className="flex items-center gap-2 text-sm mr-4">
