import React from 'react';
import { Home, MapPin, User, ShieldCheck, Star, Heart, ExternalLink } from 'lucide-react';

function formatRelativeTime(date) {
  const diffMs = Date.now() - date.getTime();
  const sec = Math.floor(diffMs / 1000);
  if (sec < 45) return 'Just now';
  const min = Math.floor(sec / 60);
  if (min < 60) return `${min}m ago`;
  const hr = Math.floor(min / 60);
  if (hr < 24) return `${hr}h ago`;
  const day = Math.floor(hr / 24);
  if (day < 7) return `${day}d ago`;
  const wk = Math.floor(day / 7);
  if (wk < 4) return `${wk}w ago`;
  const mo = Math.floor(day / 30);
  if (mo < 12) return `${mo}mo ago`;
  const yr = Math.floor(day / 365);
  return `${yr}y ago`;
}

export default function ListingCard({ listing, onClick, isFavorite, onToggleFavorite }) {
  const addressLine = [listing.streetAddress, listing.location].filter(Boolean).join(', ');
  const googleMapsQuery = [addressLine || listing.location, 'South Africa'].filter(Boolean).join(', ');
  const googleMapsUrl = googleMapsQuery
    ? `https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(googleMapsQuery)}`
    : '';
  const handleFavoriteClick = (e) => {
    e.stopPropagation();
    if (onToggleFavorite) onToggleFavorite(listing.id);
  };

  // Calculate average rating
  const reviews = listing.reviews || [];
  const avgRating = reviews.length > 0
    ? (reviews.reduce((sum, r) => sum + (r.rating || 0), 0) / reviews.length).toFixed(1)
    : null;

  return (
    <div
      onClick={onClick}
      className={`bg-white rounded-lg shadow hover:shadow-xl transition-all duration-200 cursor-pointer overflow-hidden border border-gray-100 hover:border-teal-400 group ${listing.premium ? 'ring-2 ring-yellow-400' : ''}`}
    >
      {listing.photos && listing.photos.length > 0 ? (
        <div className="relative bg-gray-200 aspect-video overflow-hidden">
          <img
            src={listing.photos[0]}
            alt="Room"
            className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300 skeleton"
            loading="lazy"
            onLoad={(e) => e.target.classList.remove('skeleton')}
          />
          {onToggleFavorite && (
            <button
              onClick={handleFavoriteClick}
              className={`absolute top-3 left-3 rounded-full p-2 shadow-lg transition-all duration-300 hover:scale-110 active:scale-95 z-10 ${
                isFavorite 
                  ? 'bg-red-50 hover:bg-red-100' 
                  : 'bg-white/90 backdrop-blur-sm hover:bg-white'
              }`}
              aria-label={isFavorite ? 'Remove from favorites' : 'Add to favorites'}
            >
              <Heart
                className={`w-5 h-5 transition-all duration-300 ${
                  isFavorite 
                    ? 'fill-red-500 text-red-500 scale-110' 
                    : 'text-gray-600 hover:text-red-400'
                }`}
              />
            </button>
          )}
          <div className="absolute top-3 right-3 flex gap-2 flex-wrap justify-end">
            {listing.photos.length > 1 && (
              <div className="bg-black bg-opacity-60 text-white px-2 py-1 rounded-md text-xs font-semibold">
                {listing.photos.length} ðŸ“¸
              </div>
            )}
            <div
              className={`px-2 py-1 rounded-md text-xs font-semibold ${
                listing.status === 'available' ? 'bg-green-500 text-white' : 'bg-gray-700 text-white'
              }`}
            >
              {listing.status === 'available' ? 'Available' : 'Rented'}
            </div>
            {listing.createdAt && Date.now() - new Date(listing.createdAt).getTime() < 1000 * 60 * 60 * 24 * 7 && (
              <div className="px-2 py-1 rounded-md text-xs font-semibold bg-teal-600 text-white shadow">New</div>
            )}
            {(() => {
              const verified = listing.landlordVerified || (listing.landlord && listing.landlord.idNumber);
              return verified;
            })() && (
              <div className="flex items-center gap-1 px-2 py-1 rounded-md text-xs font-semibold bg-blue-600 text-white shadow">
                <ShieldCheck className="w-3 h-3" /> Verified
              </div>
            )}
            {listing.premium && (
              <div className="px-2 py-1 rounded-md text-xs font-semibold bg-yellow-400 text-yellow-900 shadow">
                Premium
              </div>
            )}
          </div>
          <div className="absolute inset-0 bg-black opacity-0 group-hover:opacity-5 transition-opacity" />
          <div className="absolute bottom-2 left-2 flex items-center gap-2">
            <div className="w-8 h-8 rounded-full bg-white shadow ring-1 ring-gray-200 overflow-hidden flex items-center justify-center">
              {listing.landlordPhoto ? (
                <img src={listing.landlordPhoto} alt={listing.landlordName} className="w-full h-full object-cover" />
              ) : (
                <User className="w-5 h-5 text-gray-500" />
              )}
            </div>
            {listing.landlordName && (
              <span className="hidden sm:inline-block text-[11px] font-medium bg-white/80 backdrop-blur-sm px-2 py-1 rounded-md text-gray-700 shadow">
                {listing.landlordName.split(' ')[0]}
              </span>
            )}
            {listing.premium && (
              <span className="inline-flex items-center gap-1 text-[10px] font-semibold bg-yellow-400 text-gray-900 px-2 py-1 rounded-md shadow">
                <Star className="w-3 h-3" /> Premium
              </span>
            )}
          </div>
        </div>
      ) : (
        <div className="bg-gradient-to-br from-gray-200 to-gray-300 aspect-video flex items-center justify-center relative">
          <Home className="w-12 h-12 text-gray-400" />
          <div
            className={`absolute top-3 right-3 px-2 py-1 rounded-md text-xs font-semibold ${
              listing.status === 'available' ? 'bg-green-500 text-white' : 'bg-gray-700 text-white'
            }`}
          >
            {listing.status === 'available' ? 'Available' : 'Rented'}
          </div>
        </div>
      )}
      <div className="p-4">
        <div className="mb-3 flex items-start justify-between gap-2">
          <div className="flex-1">
            <h3 className="font-bold text-lg text-gray-800 group-hover:text-teal-600 transition mb-1 line-clamp-2">
              {listing.title}
            </h3>
            <div className="flex items-center gap-2 mb-1">
              <p className="text-red-600 font-bold text-xl">R{listing.price.toLocaleString()}/month</p>
              {avgRating && (
                <div className="flex items-center gap-1 text-sm">
                  <Star className="w-4 h-4 fill-yellow-400 text-yellow-400" />
                  <span className="font-semibold text-gray-800">{avgRating}</span>
                  <span className="text-gray-500">({reviews.length})</span>
                </div>
              )}
            </div>
          </div>
          {listing.createdAt && (
            <span className="text-[11px] font-medium text-gray-600 bg-gray-50 px-2 py-1 rounded-md self-start">
              {formatRelativeTime(new Date(listing.createdAt))}
            </span>
          )}
        </div>
        {listing.paymentMethod && (
          <div className="mb-2">
            <span className="inline-block bg-green-50 text-green-700 text-xs px-2 py-1 rounded-md font-medium">
              {listing.paymentMethod}
            </span>
          </div>
        )}
        <div className="mb-3 pb-3 border-b border-gray-100">
          <div className="flex items-center text-gray-600 text-sm">
            <MapPin className="w-4 h-4 mr-2 text-teal-600 flex-shrink-0" />
            <span className="truncate">
              {addressLine || listing.location || 'Address coming soon'}
            </span>
          </div>
          {googleMapsUrl && (
            <a
              href={googleMapsUrl}
              target="_blank"
              rel="noopener noreferrer"
              onClick={(e) => e.stopPropagation()}
              className="mt-2 inline-flex items-center gap-1 text-xs font-semibold text-teal-700 hover:text-teal-900"
            >
              <ExternalLink className="w-3 h-3" /> Open in Google Maps
            </a>
          )}
        </div>
        <p className="text-gray-600 text-sm line-clamp-2 mb-3">{listing.description}</p>
        {listing.amenities && listing.amenities.length > 0 && (
          <div className="flex flex-wrap gap-1 mb-4">
            {listing.amenities.slice(0, 3).map((amenity, index) => (
              <span
                key={index}
                className="inline-block bg-teal-50 text-teal-700 text-xs px-2 py-1 rounded-full font-medium"
              >
                {amenity}
              </span>
            ))}
            {listing.amenities.length > 3 && (
              <span className="inline-block bg-gray-100 text-gray-700 text-xs px-2 py-1 rounded-full font-medium">
                +{listing.amenities.length - 3}
              </span>
            )}
          </div>
        )}
        <button className="w-full bg-teal-600 hover:bg-teal-700 text-white py-2.5 rounded-lg font-semibold text-sm transition-all duration-200 shadow-sm hover:shadow-lg hover:scale-[1.02] active:scale-[0.98] flex items-center justify-center gap-2 group">
          <span>View Details</span>
          <svg className="w-4 h-4 transform group-hover:translate-x-1 transition-transform" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5l7 7-7 7" />
          </svg>
        </button>
      </div>
    </div>
  );
}
