                <Phone className="absolute left-4 top-3.5 w-5 h-5 text-gray-400" />
                <input
                  type="tel"
                  value={formData.whatsapp}
                  onChange={(e) => setFormData({ ...formData, whatsapp: e.target.value })}
                  placeholder="e.g., +27 71 234 5678"
                  className="w-full pl-11 pr-4 py-3 border border-gray-300 bg-white text-gray-800 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition placeholder-gray-400"
                />
              </div>
              <p className="text-xs text-gray-500 mt-1">Used to create a WhatsApp chat link on your listings.</p>
            </div>

            {/* Action Buttons */}
            <div className="flex gap-3 pt-6 border-t border-gray-200">
              <button
                onClick={onCancel}
                className="flex-1 bg-gray-100 hover:bg-gray-200 text-gray-800 font-semibold py-3 rounded-lg transition"
              >
                Cancel
              </button>
              <button
                onClick={handleSubmit}
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 rounded-lg transition shadow-sm"
              >
                Save Changes
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

/* BrowseView extracted to components/BrowseView.jsx */

function FavoritesView({ listings, onSelectListing }) {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    try {
      const rawFavorites = localStorage.getItem('favorites');
      if (rawFavorites) {
        const arr = JSON.parse(rawFavorites);
        if (Array.isArray(arr)) setFavorites(arr);
      }
    } catch {}
  }, []);

  const toggleFavorite = (listingId) => {
    setFavorites(prev => {
      const updated = prev.filter(id => id !== listingId);
      try {
        localStorage.setItem('favorites', JSON.stringify(updated));
      } catch {}
      return updated;
    });
  };

  const favoriteListings = listings.filter(l => favorites.includes(l.id));

  return (
    <div className="p-4 min-h-screen bg-gray-50 pb-20">
      <div className="max-w-7xl mx-auto mt-8">
        <div className="mb-6">
          <h2 className="text-3xl font-bold text-gray-800 flex items-center gap-3">
            <Heart className="w-8 h-8 text-red-500 fill-red-500" />
            Saved Listings
          </h2>
          <p className="text-gray-600 mt-2">
            {favoriteListings.length === 0
              ? 'No saved listings yet. Browse and tap the heart icon to save your favorites!'
              : `${favoriteListings.length} saved ${favoriteListings.length === 1 ? 'listing' : 'listings'}`}
          </p>
        </div>

        {favoriteListings.length === 0 ? (
          <div className="bg-white rounded-lg border border-gray-200 p-12 text-center">
            <Heart className="w-24 h-24 mx-auto mb-4 text-gray-300" />
            <p className="text-gray-600 font-medium mb-2">No saved listings</p>
            <p className="text-gray-500 text-sm mb-6">
              Start browsing and save your favorite rooms to find them here later
            </p>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {favoriteListings.map(listing => {
              const ListingCard = require('./components/ListingCard').default;
              return (
                <ListingCard
                  key={listing.id}
                  listing={listing}
                  onClick={() => onSelectListing(listing)}
                  isFavorite={true}
                  onToggleFavorite={toggleFavorite}
                />
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}

function AddListingView({ onSubmit, onCancel, currentUser, userType, onRequireAuth, onRequireOnboarding }) {
  const GEOCODER_KEY = process.env.REACT_APP_GEOCODER_API_KEY;
  const GEOCODER_PROVIDER = (process.env.REACT_APP_GEOCODER_PROVIDER || '').toLowerCase();

  const [formData, setFormData] = useState({
    title: '',
    price: '',
    location: '',
    streetAddress: '',
    description: '',
    photos: [],
    status: 'available',
    availableDate: new Date().toISOString().split('T')[0],
    amenities: [],
    latitude: null,
    longitude: null,
    paymentMethod: 'Bank and Cash',
    roomType: 'private',
    leaseDuration: '7-12',
    petFriendly: false,
    genderPreference: 'any'
  });
  const [geocodingStatus, setGecodingStatus] = useState('');
  const [errors, setErrors] = useState({});
  const [locationHelpBanner, setLocationHelpBanner] = useState('');
  const [locationSuggestions, setLocationSuggestions] = useState([]);
  const [locationSuggestionsLoading, setLocationSuggestionsLoading] = useState(false);
  const locationSuggestionCacheRef = React.useRef({}); // { queryLower: { data, ts } }
  const LOCATION_SUGGESTION_TTL_MS = 15 * 60 * 1000;

  const availableAmenities = [
    'WiFi', 'Parking', 'Kitchen', 'Laundry', 'Air Conditioning', 
    'Heating', 'TV', 'Furnished', 'Pet Friendly', 'Garden'
  ];

  const validateField = (name, value) => {
    const newErrors = { ...errors };

    switch (name) {
      case 'title':
        if (!value || value.trim().length < 5) {
          newErrors.title = 'Title must be at least 5 characters';
        } else {
          delete newErrors.title;
        }
        break;
      case 'price':
        if (!value || parseFloat(value) <= 0) {
          newErrors.price = 'Price must be greater than 0';
        } else if (parseFloat(value) < 500) {
          newErrors.price = 'Price seems too low (minimum R500)';
        } else if (parseFloat(value) > 50000) {
          newErrors.price = 'Price seems too high (maximum R50,000)';
        } else {
          delete newErrors.price;
        }
        break;
      case 'location':
        if (!value || value.trim().length < 3) {
          newErrors.location = 'Location is required (min 3 characters)';
        } else {
          delete newErrors.location;
        }
        break;
      case 'description':
        if (value && value.length > 500) {
          newErrors.description = 'Description is too long (max 500 characters)';
        } else {
          delete newErrors.description;
        }
        break;
      default:
        break;
    }

    setErrors(newErrors);
  };

  // Debounced provider-aware suggestions (Mapbox/Nominatim) with caching & bias
  useEffect(() => {
    const raw = (formData.location || '').trim();
    if (raw.length < 3) { setLocationSuggestions([]); setLocationSuggestionsLoading(false); return; }
    const qLower = raw.toLowerCase();
    const cached = locationSuggestionCacheRef.current[qLower];
    if (cached && (Date.now() - cached.ts) < LOCATION_SUGGESTION_TTL_MS) {
      setLocationSuggestions(cached.data);
      return;
    }
    const controller = new AbortController();
    setLocationSuggestionsLoading(true);
    const t = setTimeout(async () => {
      try {
        let suggestions = [];
        if (GEOCODER_PROVIDER === 'mapbox' && GEOCODER_KEY) {
          const proximity = (formData.latitude && formData.longitude) ? `&proximity=${formData.longitude},${formData.latitude}` : '';
          const url = `https://api.mapbox.com/geocoding/v5/mapbox.places/${encodeURIComponent(raw)}.json?access_token=${GEOCODER_KEY}&limit=5&country=ZA&language=en${proximity}&types=place,locality,neighborhood`;
          const resp = await fetch(url, { signal: controller.signal });
          const data = await resp.json();
          if (data && Array.isArray(data.features)) {
            suggestions = data.features.map(f => f.place_name).filter(Boolean);
          }
        } else {
          let biasParams = '';
          if (formData.latitude && formData.longitude) {
            const delta = 0.2;
            const minLon = formData.longitude - delta;
            const maxLon = formData.longitude + delta;
            const minLat = formData.latitude - delta;
            const maxLat = formData.latitude + delta;
            biasParams = `&bounded=1&viewbox=${minLon},${maxLat},${maxLon},${minLat}`;
          }
          const url = `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(raw + ', South Africa')}&limit=5${biasParams}`;
          const resp = await fetch(url, { signal: controller.signal, headers: { 'Accept-Language': 'en' } });
          const data = await resp.json();
          const uniq = [];
          data.forEach(item => {
            const display = item.display_name || '';
            const parts = display.split(',').map(p => p.trim());
            const area = parts.slice(0, 2).join(', ');
            if (area && !uniq.includes(area)) uniq.push(area);
          });
          suggestions = uniq.slice(0,5);
        }
        locationSuggestionCacheRef.current[qLower] = { data: suggestions, ts: Date.now() };
        setLocationSuggestions(suggestions);
      } catch (e) {
        setLocationSuggestions([]);
      } finally {
        setLocationSuggestionsLoading(false);
      }
    }, 500);
    return () => { clearTimeout(t); controller.abort(); };
  }, [formData.location, formData.latitude, formData.longitude, GEOCODER_PROVIDER, GEOCODER_KEY, LOCATION_SUGGESTION_TTL_MS]);

  // Geocode address using Nominatim (OpenStreetMap)
  // Use browser geolocation and reverse-geocode to fill address/coords
  const geolocateCurrentPosition = () => {
    const insecure = (typeof window !== 'undefined') && !window.isSecureContext && window.location.hostname !== 'localhost';
    if (insecure) {
      setGecodingStatus('Location is blocked on HTTP. Use HTTPS to enable.');
      setLocationHelpBanner('Your browser requires HTTPS to access GPS on mobile. Use the HTTPS link below.');
      return;
    }
    if (!navigator.geolocation) {
      setGecodingStatus('Geolocation not supported by your browser');
      setLocationHelpBanner('Location is disabled. Tap for steps to enable.');
      return;
    }

    setGecodingStatus('Getting your precise location...');
    navigator.geolocation.getCurrentPosition(async (pos) => {
      const lat = pos.coords.latitude;
      const lon = pos.coords.longitude;
      const accuracy = pos.coords.accuracy;

      console.log(`Location obtained: ${lat}, ${lon} (accuracy: ${accuracy}m)`);
      setFormData(prev => ({ ...prev, latitude: lat, longitude: lon }));

      // Reverse geocode to get a readable address
      try {
        if (GEOCODER_PROVIDER === 'mapbox' && GEOCODER_KEY) {
          const rev = await fetch(`https://api.mapbox.com/geocoding/v5/mapbox.places/${lon},${lat}.json?access_token=${GEOCODER_KEY}&limit=1&country=za&types=address,poi`);
          const revData = await rev.json();
          if (revData && revData.features && revData.features.length > 0) {
            const feature = revData.features[0];
            const address = feature.address || '';
            const streetName = feature.text || '';
            const fullStreet = address ? `${address} ${streetName}` : streetName;

            // Extract area from context
            let area = '';
            if (feature.context) {
              const place = feature.context.find(c => c.id.startsWith('place'));
              const locality = feature.context.find(c => c.id.startsWith('locality'));
              area = place ? place.text : (locality ? locality.text : '');
            }

            setFormData(prev => ({ 
              ...prev, 
              streetAddress: fullStreet || prev.streetAddress, 
              location: area || prev.location,
              latitude: lat,
              longitude: lon
            }));
            setGecodingStatus(` Location set (${accuracy.toFixed(0)}m accuracy)`);
            setTimeout(() => setGecodingStatus(''), 3000);
            return;
          }
        }

        // Nominatim fallback with detailed addressing
        const rev = await fetch(`https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lon}&zoom=18&addressdetails=1`);
        const revData = await rev.json();
        if (revData && revData.address) {
          const addr = revData.address;
          // Build street address from components
          const houseNumber = addr.house_number || '';
          const road = addr.road || addr.street || '';
          const suburb = addr.suburb || addr.neighbourhood || '';
          const street = [houseNumber, road].filter(Boolean).join(' ').trim() || suburb;

          // Build area from city/town components
          const city = addr.city || addr.town || addr.village || '';
          const municipality = addr.municipality || '';
          const area = city || municipality || suburb;

          setFormData(prev => ({ 
            ...prev, 
            streetAddress: street || prev.streetAddress, 
            location: area || prev.location,
            latitude: lat,
            longitude: lon
          }));
          setGecodingStatus(` Location set (${accuracy.toFixed(0)}m accuracy)`);
          setTimeout(() => setGecodingStatus(''), 3000);
          return;
        }

      } catch (e) {
        console.error('Reverse geocode failed', e);
      }

      // Even if reverse geocoding fails, we have accurate coordinates
      setGecodingStatus(` Precise coordinates set (${accuracy.toFixed(0)}m accuracy)`);
      setTimeout(() => setGecodingStatus(''), 3000);
    }, (err) => {
      console.error('Geolocation error', err);
      if (err && (err.code === 1)) {
        // PERMISSION_DENIED
        setGecodingStatus('Permission denied. Allow location access to continue.');
        setLocationHelpBanner('Tap the lock icon in the address bar and set Location to Allow, then refresh.');
      } else if (err && (err.code === 2)) {
        // POSITION_UNAVAILABLE
        setGecodingStatus('Position unavailable. Check GPS and network, then retry.');
      } else if (err && (err.code === 3)) {
        // TIMEOUT
        setGecodingStatus('Location timed out. Move to open area or retry.');
      } else {
        setGecodingStatus('Could not get your location - ' + (err?.message || 'Unknown error'));
      }
      setLocationHelpBanner('Location permission denied. Tap here for steps to enable.');
    }, { 
      enableHighAccuracy: true, 
      timeout: 15000, 
      maximumAge: 0 
    });
  };

  const geocodeAddress = async () => {
    if (!formData.streetAddress) {
      setGecodingStatus('Please enter a street address');
      return;
    }

    setGecodingStatus('Finding coordinates...');
    try {
      const fullAddress = `${formData.streetAddress}, ${formData.location}, South Africa`;
      // Try Mapbox if configured
      if (GEOCODER_PROVIDER === 'mapbox' && GEOCODER_KEY) {
        try {
          const resp = await fetch(`https://api.mapbox.com/geocoding/v5/mapbox.places/${encodeURIComponent(fullAddress)}.json?access_token=${GEOCODER_KEY}&limit=1&country=za`);
          const data = await resp.json();
          if (data && data.features && data.features.length > 0) {
            const [lon, lat] = data.features[0].center || [];
            setFormData({ ...formData, latitude: parseFloat(lat), longitude: parseFloat(lon) });
            setGecodingStatus(' Address found (Mapbox)!');
            setTimeout(() => setGecodingStatus(''), 2000);
            return;
          }
        } catch (err) {
          console.warn('Mapbox geocoding failed, falling back to Nominatim', err);
        }
      }

      // Default / fallback: Nominatim
      const response = await fetch(
        `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(fullAddress)}`
      );
      const results = await response.json();

      if (results.length > 0) {
        const { lat, lon } = results[0];
        setFormData({ 
          ...formData, 
          latitude: parseFloat(lat), 
          longitude: parseFloat(lon) 
        });
        setGecodingStatus(' Address found!');
        setTimeout(() => setGecodingStatus(''), 2000);
      } else {
        setGecodingStatus('Address not found. Trying with area only...');
        // Fallback: just use the area/city
        const areaResponse = await fetch(
          `https://nominatim.openstreetmap.org/search?format=json&q=${encodeURIComponent(formData.location + ', South Africa')}`
        );
        const areaResults = await areaResponse.json();
        if (areaResults.length > 0) {
          const { lat, lon } = areaResults[0];
          setFormData({ 
            ...formData, 
            latitude: parseFloat(lat), 
            longitude: parseFloat(lon) 
          });
          setGecodingStatus(' Area found!');
          setTimeout(() => setGecodingStatus(''), 2000);
        } else {
          setGecodingStatus('Could not find location');
        }
      }
    } catch (error) {
      console.error('Geocoding error:', error);
      setGecodingStatus('Error finding address. Check your connection.');
    }
  };

  // Draggable preview marker component for Add Listing
  function DraggablePreview({ lat, lng }) {
    const [pos, setPos] = useState(lat && lng ? [lat, lng] : null);
    useEffect(() => {
      if (lat && lng) setPos([lat, lng]);
    }, [lat, lng]);

    if (!pos) return null;
    return (
      <MapContainer center={pos} zoom={15} className="w-full h-48 rounded-lg" whenCreated={() => {}}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        <Marker
          position={pos}
          draggable={true}
          eventHandlers={{
            dragend: (e) => {
              const ll = e.target.getLatLng();
              setPos([ll.lat, ll.lng]);
              setFormData(prev => ({ ...prev, latitude: ll.lat, longitude: ll.lng }));
            }
          }}
        />
      </MapContainer>
    );
  }

  const toggleAmenity = (amenity) => {
    if (formData.amenities.includes(amenity)) {
      setFormData({ ...formData, amenities: formData.amenities.filter(a => a !== amenity) });
    } else {
      setFormData({ ...formData, amenities: [...formData.amenities, amenity] });
    }
  };

  const handlePhotoUpload = (e) => {
    const files = Array.from(e.target.files || []);
    if (files.length === 0) return;

    // Compress each image before adding
    Promise.all(
      files.map(file => compressImage(file, { maxWidth: 1200, maxHeight: 1200, quality: 0.8, maxSizeKB: 500 }))
    ).then(images => {
      const combined = [...formData.photos, ...images].slice(0, 5); // cap at 5
      setFormData({ ...formData, photos: combined });
    }).catch(err => {
      console.error('Image compression failed:', err);
      // Fallback to regular loading if compression fails
      const readers = files.map(file => new Promise((resolve) => {
        const reader = new FileReader();
        reader.onloadend = () => resolve(reader.result);
        reader.readAsDataURL(file);
      }));
      Promise.all(readers).then(images => {
        const combined = [...formData.photos, ...images].slice(0, 5);
        setFormData({ ...formData, photos: combined });
      });
    });
  };

  const removePhoto = (index) => {
    const newPhotos = formData.photos.filter((_, i) => i !== index);
    setFormData({ ...formData, photos: newPhotos });
  };

  const handleSubmit = () => {
    // Enforce auth and onboarding checks before allowing submission
    if (!currentUser) {
      onRequireAuth && onRequireAuth('landlord');
      return;
    }
    if (userType !== 'landlord') {
      // prompt to become landlord
      onRequireAuth && onRequireAuth('landlord');
      return;
    }
    if (!currentUser.landlordComplete) {
      // send to onboarding
      onRequireOnboarding && onRequireOnboarding();
      return;
    }

    // Validate all fields
    validateField('title', formData.title);
    validateField('price', formData.price);
    validateField('location', formData.location);
    validateField('description', formData.description);

    if (!formData.title || formData.title.trim().length < 5) {
      setErrors(prev => ({ ...prev, title: 'Title must be at least 5 characters' }));
      return;
    }
    if (!formData.price || parseFloat(formData.price) <= 0) {
      setErrors(prev => ({ ...prev, price: 'Price must be greater than 0' }));
      return;
    }
    if (!formData.location || formData.location.trim().length < 3) {
      setErrors(prev => ({ ...prev, location: 'Location is required' }));
      return;
    }
    if (Object.keys(errors).length > 0) {
      return;
    }

    onSubmit(formData);
  };

  // Auto-attempt geolocation shortly after mount for landlord convenience
  useEffect(() => {
    const shouldAttempt = !formData.latitude && !formData.longitude;
    if (shouldAttempt) {
      const t = setTimeout(() => {
        console.log('Auto-triggering current location detection...');
        geolocateCurrentPosition();
      }, 500); // Faster trigger
      return () => clearTimeout(t);
    }
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [formData.latitude, formData.longitude]);

  return (
    <div className="p-4 bg-gray-50 min-h-screen pb-20">
      <div className="max-w-3xl mx-auto mt-8">
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-8">
          <div className="flex items-center justify-between mb-8">
            <h2 className="text-3xl font-bold text-gray-800">Post a Room</h2>
            <button onClick={onCancel} className="text-gray-400 hover:text-gray-600 transition">
              <X className="w-6 h-6" />
            </button>
          </div>

          <div className="space-y-6">
            {/* Title */}
            <div>
              <label className="block text-sm font-semibold text-gray-800 mb-2">Room Title *</label>
              <input
                type="text"
                required
                value={formData.title}
                onChange={(e) => {
                  setFormData({ ...formData, title: e.target.value });
                  validateField('title', e.target.value);
                }}
                onBlur={(e) => validateField('title', e.target.value)}
                placeholder="e.g., Cozy backroom with bathroom"
                className={`w-full px-4 py-3 border ${errors.title ? 'border-red-500' : 'border-gray-300'} bg-white text-gray-800 rounded-lg focus:ring-2 ${errors.title ? 'focus:ring-red-500' : 'focus:ring-blue-500'} focus:border-transparent transition placeholder-gray-400`}
              />
              {errors.title && <p className="text-red-500 text-xs mt-1">{errors.title}</p>}
            </div>

            {/* Price */}
            <div>
              <label className="block text-sm font-semibold text-gray-800 mb-2">Monthly Price (R) *</label>
              <input
                type="number"
                required
                value={formData.price}
                onChange={(e) => {
                  setFormData({ ...formData, price: e.target.value });
                  validateField('price', e.target.value);
                }}
                onBlur={(e) => validateField('price', e.target.value)}
                placeholder="e.g., 2500"
                className={`w-full px-4 py-3 border ${errors.price ? 'border-red-500' : 'border-gray-300'} bg-white text-gray-800 rounded-lg focus:ring-2 ${errors.price ? 'focus:ring-red-500' : 'focus:ring-blue-500'} focus:border-transparent transition placeholder-gray-400`}
              />
              {errors.price && <p className="text-red-500 text-xs mt-1">{errors.price}</p>}
            </div>

            {/* Location and Street Address */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-semibold text-gray-800 mb-2">Area / City *</label>
                <input
                  type="text"
                  required
                  value={formData.location}
                  onChange={(e) => {
                    setFormData({ ...formData, location: e.target.value });
                    validateField('location', e.target.value);
                  }}
                  onBlur={(e) => validateField('location', e.target.value)}
                  placeholder="e.g., Soweto, Johannesburg"
                  className={`w-full px-4 py-3 border ${errors.location ? 'border-red-500' : 'border-gray-300'} bg-white text-gray-800 rounded-lg focus:ring-2 ${errors.location ? 'focus:ring-red-500' : 'focus:ring-blue-500'} focus:border-transparent transition placeholder-gray-400`}
                />
                {errors.location && <p className="text-red-500 text-xs mt-1">{errors.location}</p>}
                {locationSuggestionsLoading && (
                  <p className="text-xs text-blue-600 mt-1 animate-pulse" aria-live="polite">Loading suggestions...</p>
                )}
                {locationSuggestions.length > 0 && (
                  <div className="mt-2 border border-gray-200 rounded-lg bg-white shadow-sm divide-y">
                    {locationSuggestions.map(s => (
                      <button
                        key={s}
                        type="button"
                        onClick={() => { setFormData({ ...formData, location: s }); setLocationSuggestions([]); validateField('location', s); }}
                        className="w-full text-left px-3 py-2 text-sm hover:bg-blue-50"
                      >{s}</button>
                    ))}
                  </div>
                )}
              </div>

              <div>
                <label className="block text-sm font-semibold text-gray-800 mb-2">Street Address</label>
                <input
                  type="text"
                  value={formData.streetAddress}
                  onChange={(e) => setFormData({ ...formData, streetAddress: e.target.value })}
                  placeholder="e.g., 123 Main Street"
                  className="w-full px-4 py-3 border border-gray-300 bg-white text-gray-800 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition placeholder-gray-400"
                />
              </div>
            </div>

            {/* Geocode Section - Prominent Auto-Location */}
            <div className="bg-gradient-to-br from-blue-50 to-indigo-50 border-2 border-blue-300 rounded-lg p-6">
              <div className="flex items-start gap-3 mb-4">
                <MapPin className="w-6 h-6 text-blue-600 flex-shrink-0 mt-1" />
                <div className="flex-1">
                  <h3 className="text-lg font-semibold text-gray-800 mb-1">Set Your Precise Location</h3>
                  <p className="text-sm text-gray-600">For best accuracy, use your current GPS location. The exact address will be automatically detected.</p>
                </div>
              </div>

              <div className="flex flex-wrap gap-3 items-center mb-4">
                {locationHelpBanner && (
                  <div className="w-full bg-yellow-50 border-2 border-yellow-200 text-yellow-800 rounded-lg p-3 text-sm flex items-start gap-2">
                    <span></span>
                    <div className="flex-1">
                      {locationHelpBanner}
                      <button
                        type="button"
                        className="ml-2 underline font-semibold"
                        onClick={() => window.alert('To enable location: 1) Tap the lock icon in the address bar. 2) Find Location permission. 3) Set to Allow. Then refresh the page.')}
                      >How to enable</button>
                    </div>
                  </div>
                )}
                <div className="w-full flex items-start gap-2 text-xs text-blue-800 bg-white border border-blue-300 rounded p-3">
                  <span className="text-lg"></span>
                  <span><strong>Recommended:</strong> Click "Use Current Location" for GPS accuracy (within meters). You can then drag the pin on the map to fine-tune if needed.</span>
                </div>

                {/* Primary action - Current Location */}
                <button
                  type="button"
                  onClick={geolocateCurrentPosition}
                  className="w-full md:w-auto bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white px-8 py-3 rounded-lg font-bold transition shadow-md flex items-center justify-center gap-2 text-base"
                >
                  <MapPin className="w-5 h-5" />
                  Use Current Location (Recommended)
                </button>

                {/* Secondary action - Manual address lookup */}
                <button
                  type="button"
                  onClick={geocodeAddress}
                  className="w-full md:w-auto bg-white hover:bg-gray-50 text-gray-700 border-2 border-gray-300 px-6 py-3 rounded-lg text-sm font-semibold transition flex items-center justify-center gap-2"
                >
                  <MapPin className="w-4 h-4" />
                  Find Address on Map
                </button>

                <div className="text-sm flex-1">
                  {geocodingStatus && (
                    <span className={`${geocodingStatus.startsWith('') ? 'text-green-600 font-semibold' : 'text-gray-700'} flex items-center gap-1`}>
                      {geocodingStatus.startsWith('') && <CheckCircle className="w-4 h-4" />}
                      {geocodingStatus}
                    </span>
                  )}
                  {formData.latitude && formData.longitude && !geocodingStatus && (
                    <span className="text-green-600 font-semibold flex items-center gap-1">
                      <CheckCircle className="w-4 h-4" />
                      Location set: {formData.latitude.toFixed(4)}, {formData.longitude.toFixed(4)}
                    </span>
                  )}
                </div>
              </div>

              {/* Draggable preview map */}
              <div className="mt-4">
                {(formData.latitude && formData.longitude) ? (
                  <div>
                    <label className="block text-sm font-semibold text-gray-800 mb-2">Fine-tune Location (drag pin)</label>
                    <DraggablePreview lat={formData.latitude} lng={formData.longitude} />
                  </div>
                ) : (
                  <div className="text-sm text-gray-600 bg-white border border-gray-200 rounded-lg p-4 text-center">
                    <MapPin className="w-8 h-8 mx-auto mb-2 text-gray-400" />
                    No location set yet. Click "Find on Map" or "Use Current Location" to place a pin.
                  </div>
                )}
              </div>
            </div>

            {/* Status and Available Date - Two columns */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-semibold text-gray-800 mb-2">Status</label>
                <select
                  value={formData.status}
                  onChange={(e) => setFormData({ ...formData, status: e.target.value })}
                  className="w-full px-4 py-3 border border-gray-300 bg-white text-gray-800 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition"
                >
                  <option value="available">Available</option>
                  <option value="rented">Rented</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-semibold text-gray-800 mb-2">Available Date</label>
                <input
                  type="date"
                  value={formData.availableDate}
                  onChange={(e) => setFormData({ ...formData, availableDate: e.target.value })}
                  className="w-full px-4 py-3 border border-gray-300 bg-white text-gray-800 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition"
                />
              </div>
            </div>

            {/* Payment Method */}
            <div>
              <label className="block text-sm font-semibold text-gray-800 mb-2">Payment Method</label>
              <select
                value={formData.paymentMethod}
                onChange={(e) => setFormData({ ...formData, paymentMethod: e.target.value })}
                className="w-full px-4 py-3 border border-gray-300 bg-white text-gray-800 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition"
              >
                <option value="Bank and Cash">Bank and Cash</option>
                <option value="Cash Only">Cash Only</option>
                <option value="Bank Only">Bank Only</option>
              </select>
            </div>

            {/* Room Type and Lease Duration - Two columns */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-semibold text-gray-800 mb-2">Room Type</label>
                <select
                  value={formData.roomType}
                  onChange={(e) => setFormData({ ...formData, roomType: e.target.value })}
                  className="w-full px-4 py-3 border border-gray-300 bg-white text-gray-800 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition"
                >
                  <option value="private">Private Room</option>
                  <option value="shared">Shared Room</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-semibold text-gray-800 mb-2">Lease Duration (months)</label>
                <select
                  value={formData.leaseDuration}
                  onChange={(e) => setFormData({ ...formData, leaseDuration: e.target.value })}
                  className="w-full px-4 py-3 border border-gray-300 bg-white text-gray-800 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition"
                >
                  <option value="1-3">1-3 months</option>
                  <option value="4-6">4-6 months</option>
                  <option value="7-12">7-12 months</option>
                  <option value="12+">12+ months</option>
                </select>
              </div>
            </div>

            {/* Pet Friendly and Gender Preference */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="flex items-center gap-3">
                <input
                  type="checkbox"
                  checked={formData.petFriendly}
                  onChange={(e) => setFormData({ ...formData, petFriendly: e.target.checked })}
                  id="petFriendly"
                  className="w-4 h-4 text-blue-600 border-gray-300 rounded focus:ring-blue-500"
                />
                <label htmlFor="petFriendly" className="text-sm font-medium text-gray-700">
                   Pet Friendly
                </label>
              </div>

              <div>
                <label className="block text-sm font-semibold text-gray-800 mb-2">Gender Preference</label>
                <select
                  value={formData.genderPreference}
                  onChange={(e) => setFormData({ ...formData, genderPreference: e.target.value })}
                  className="w-full px-4 py-3 border border-gray-300 bg-white text-gray-800 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition"
                >
                  <option value="any">Any</option>
                  <option value="male">Male Only</option>
                  <option value="female">Female Only</option>
                </select>
              </div>
            </div>

            {/* Premium Flag */}
            <div className="flex items-center gap-3">
              <input
                type="checkbox"
                checked={!!formData.premium}
                onChange={(e) => setFormData({ ...formData, premium: e.target.checked })}
                id="premiumFlag"
              />
              <label htmlFor="premiumFlag" className="text-sm font-medium text-gray-700">
                Premium listing (highlight & priority sorting)
              </label>
            </div>

            {/* Description */}
            <div>
              <label className="block text-sm font-semibold text-gray-800 mb-2">
                Description 
                {formData.description && (
                  <span className={`ml-2 text-xs ${formData.description.length > 500 ? 'text-red-500' : 'text-gray-500'}`}>
                    ({formData.description.length}/500 characters)
                  </span>
                )}
              </label>
              <textarea
                value={formData.description}
                onChange={(e) => {
                  setFormData({ ...formData, description: e.target.value });
                  validateField('description', e.target.value);
                }}
                onBlur={(e) => validateField('description', e.target.value)}
                placeholder="Describe the room, amenities, rules, and any other details..."
                rows="4"
                className={`w-full px-4 py-3 border ${errors.description ? 'border-red-500' : 'border-gray-300'} bg-white text-gray-800 rounded-lg focus:ring-2 ${errors.description ? 'focus:ring-red-500' : 'focus:ring-blue-500'} focus:border-transparent transition placeholder-gray-400`}
              />
              {errors.description && <p className="text-red-500 text-xs mt-1">{errors.description}</p>}
            </div>

            {/* Amenities */}
            <div>
              <label className="block text-sm font-semibold text-gray-800 mb-3">Amenities</label>
              <div className="grid grid-cols-2 md:grid-cols-3 gap-2">
                {availableAmenities.map(amenity => (
                  <button
                    key={amenity}
                    type="button"
                    onClick={() => toggleAmenity(amenity)}
                    className={`px-4 py-2 rounded-lg text-sm font-medium transition ${
                      formData.amenities.includes(amenity)
                        ? 'bg-blue-600 text-white shadow-sm'
                        : 'bg-gray-100 text-gray-700 border border-gray-300 hover:bg-gray-200'
                    }`}
                  >
                    {amenity}
                  </button>
                ))}
              </div>
            </div>

            {/* Photos */}
            <div>
              <label className="block text-sm font-semibold text-gray-800 mb-2">Photos (Max 5)</label>
              <input
                type="file"
                accept="image/*"
                multiple
                onChange={handlePhotoUpload}
                disabled={formData.photos.length >= 5}
                className="w-full px-4 py-3 border border-gray-300 bg-white text-gray-700 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition cursor-pointer"
              />
              {formData.photos.length > 0 && (
                <div className="mt-4 grid grid-cols-2 md:grid-cols-3 gap-3">
                  {formData.photos.map((photo, index) => (
                    <div key={index} className="relative group">
                      <img 
                        src={photo} 
                        alt={`Preview ${index + 1}`} 
                        className="w-full h-32 object-cover rounded-lg border border-gray-200 shadow-sm" 
                      />
                      <button
                        type="button"
                        onClick={() => removePhoto(index)}
                        className="absolute top-2 right-2 bg-red-500 hover:bg-red-600 text-white rounded-full w-6 h-6 flex items-center justify-center shadow-lg opacity-0 group-hover:opacity-100 transition"
                      >
                        <X className="w-4 h-4" />
                      </button>
                    </div>
                  ))}
                </div>
              )}
              {formData.photos.length >= 5 && (
                <p className="text-sm text-gray-500 mt-2">Maximum 5 photos reached</p>
              )}
            </div>

            {/* Submit Button */}
            <div className="border-t border-gray-200 pt-6">
              <button
                onClick={handleSubmit}
                className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 rounded-lg transition shadow-sm"
              >
                Post Room
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

