import React, { useState } from 'react';
import { User, Menu, X, Bell, Home, Sparkles } from 'lucide-react';

export default function Header({ currentUser, previewAsRenter, setPreviewAsRenter, openAuthModal, handleSignOut, setCurrentView, showToast, unreadCount, onOpenNotifications }) {
  const [showMobileMenu, setShowMobileMenu] = useState(false);

  return (
    <header className="bg-white/95 backdrop-blur-md border-b border-gray-100 sticky top-0 z-50 shadow-sm" role="banner">
      <div className="max-w-7xl mx-auto px-4 py-2.5 md:py-3">
        <div className="flex items-center justify-between min-h-[52px]">
          {/* Logo */}
          <button
            onClick={() => setCurrentView('browse')}
            className="text-xl md:text-2xl font-extrabold flex items-center gap-1.5 focus:outline-none focus-visible:ring-2 focus-visible:ring-teal-500 px-1 rounded-lg transition-transform hover:scale-105 active:scale-95"
            aria-label="Go to browse"
          >
            <div className="w-8 h-8 md:w-9 md:h-9 bg-gradient-to-br from-teal-500 to-teal-600 rounded-lg flex items-center justify-center shadow-md">
              <Home className="w-4 h-4 md:w-5 md:h-5 text-white" />
            </div>
            <span className="bg-gradient-to-r from-teal-600 to-cyan-600 bg-clip-text text-transparent">Room</span>
            <span className="text-rose-500">24</span>
          </button>

          {/* Desktop Right Section */}
          <div className="hidden md:flex items-center gap-4">
            {!currentUser && (
              <div className="flex items-center gap-3">
                <button onClick={() => openAuthModal('renter')} className="text-gray-600 hover:text-gray-800 px-3 py-1.5 rounded-md text-sm font-medium transition">
                  Sign in
                </button>
                <button onClick={() => openAuthModal('landlord')} className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded-md text-sm font-semibold shadow-sm transition">
                  List Your Room
                </button>
              </div>
            )}
            {currentUser && (
              <>
                {onOpenNotifications && (
                  <button
                    onClick={onOpenNotifications}
                    className="relative p-2 text-gray-600 hover:bg-gray-100 rounded-full transition-all duration-200 hover:scale-105"
                    aria-label="Notifications"
                  >
                    <Bell className={`w-5 h-5 ${unreadCount > 0 ? 'text-teal-600' : ''}`} />
                    {unreadCount > 0 && (
                      <span className="absolute -top-0.5 -right-0.5 bg-red-500 text-white text-[10px] font-bold rounded-full min-w-[18px] h-[18px] flex items-center justify-center pulse-glow shadow-sm">
                        {unreadCount > 99 ? '99+' : unreadCount}
                      </span>
                    )}
                  </button>
                )}
                <div className="flex items-center gap-2 text-sm mr-2 group cursor-pointer" onClick={() => setCurrentView('profile')}>
                  <div className="w-10 h-10 rounded-full bg-gradient-to-br from-teal-400 to-teal-600 flex items-center justify-center text-white font-bold shadow-md ring-2 ring-white group-hover:ring-teal-200 transition-all duration-200">
                    {currentUser.photo ? (
                      <img src={currentUser.photo} alt={currentUser.name} className="w-full h-full rounded-full object-cover" />
                    ) : (
                      currentUser.name.charAt(0).toUpperCase()
                    )}
                  </div>
                  <div className="hidden lg:block">
                    <p className="font-semibold text-gray-800 leading-tight group-hover:text-teal-600 transition">{currentUser.name}</p>
                    <p className="text-xs text-gray-500 leading-tight flex items-center gap-1">
                      {previewAsRenter ? (
                        <><span className="w-1.5 h-1.5 bg-amber-500 rounded-full"></span> Previewing as Renter</>
                      ) : currentUser.type === 'landlord' ? (
                        <><span className="w-1.5 h-1.5 bg-teal-500 rounded-full"></span> Property Owner</>
                      ) : (
                        <><span className="w-1.5 h-1.5 bg-blue-500 rounded-full"></span> Looking for a Room</>
                      )}
                    </p>
                  </div>
                </div>
                <button 
                  onClick={() => setCurrentView('profile')}
                  className="p-2 text-gray-600 hover:bg-gray-100 rounded-md transition" aria-label="Profile"
                >
                  <User className="w-5 h-5" />
                </button>
                {currentUser.type === 'landlord' && (
                  <button
                    onClick={() => setPreviewAsRenter(!previewAsRenter)}
                    className="text-xs px-2 py-1 rounded-md border border-teal-200 bg-teal-50 text-teal-700 hover:bg-teal-100 transition"
                    aria-pressed={previewAsRenter ? 'true' : 'false'}
                  >
                    {previewAsRenter ? 'Exit Renter Preview' : 'Preview as Renter'}
                  </button>
                )}
                <button
                  onClick={handleSignOut}
                  className="text-xs text-gray-500 hover:text-gray-700 px-2 py-1 rounded-md border border-gray-200 hover:border-gray-300 transition"
                >
                  Sign Out
                </button>
              </>
            )}
          </div>

          {/* Mobile Menu / Auth CTAs */}
          <div className="flex md:hidden items-center gap-2">
            {!currentUser ? (
              <>
                <button onClick={() => openAuthModal('renter')} className="text-sm text-gray-600 hover:text-gray-800 px-2 py-1">Sign in</button>
                <button onClick={() => openAuthModal('landlord')} className="text-sm bg-red-500 hover:bg-red-600 text-white px-3 py-1.5 rounded">List Room</button>
              </>
            ) : (
              <>
                {onOpenNotifications && (
                  <button
                    onClick={onOpenNotifications}
                    className="relative p-2 text-gray-600 hover:bg-gray-100 rounded-md transition"
                    aria-label="Notifications"
                  >
                    <Bell className="w-5 h-5" />
                    {unreadCount > 0 && (
                      <span className="absolute top-0 right-0 bg-red-500 text-white text-xs font-semibold rounded-full w-4 h-4 flex items-center justify-center">
                        {unreadCount > 9 ? '9+' : unreadCount}
                      </span>
                    )}
                  </button>
                )}
                <button
                  onClick={() => setShowMobileMenu(!showMobileMenu)}
                  className="p-2 text-gray-600 hover:bg-gray-100 rounded-md transition"
                  aria-label="Menu"
                >
                  {showMobileMenu ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
                </button>
              </>
            )}
          </div>
        </div>

        {/* Mobile Dropdown */}
        {currentUser && showMobileMenu && (
          <div className="md:hidden mt-2 pb-3 border-t border-gray-200 pt-3 animate-slideDown" role="navigation" aria-label="Mobile">
            <div className="flex items-center gap-3 mb-4 pb-3 border-b border-gray-100">
              <div className="w-10 h-10 rounded-full bg-gradient-to-br from-teal-400 to-teal-600 flex items-center justify-center text-white font-semibold shadow-sm">
                {currentUser.name.charAt(0)}
              </div>
              <div>
                <p className="font-medium text-gray-800 leading-tight">{currentUser.name}</p>
                <p className="text-xs text-gray-500 leading-tight">{currentUser.type === 'landlord' ? 'Property Owner' : 'Looking for a Room'}</p>
              </div>
            </div>
            <div className="space-y-1">
              <button 
                onClick={() => { setCurrentView('profile'); setShowMobileMenu(false); }}
                className="w-full flex items-center gap-3 px-3 py-2 text-gray-700 hover:bg-gray-100 rounded-md transition"
              >
                <User className="w-5 h-5" />
                <span>Profile</span>
              </button>
              {currentUser.type === 'landlord' && (
                <button
                  onClick={() => { setPreviewAsRenter(!previewAsRenter); setShowMobileMenu(false); }}
                  className="w-full flex items-center gap-3 px-3 py-2 text-teal-700 hover:bg-teal-50 rounded-md transition text-sm"
                  aria-pressed={previewAsRenter ? 'true' : 'false'}
                >
                  <User className="w-5 h-5" />
                  <span>{previewAsRenter ? 'Exit Renter Preview' : 'Preview as Renter'}</span>
                </button>
              )}
              <button
                onClick={() => { handleSignOut(); setShowMobileMenu(false); }}
                className="w-full flex items-center gap-3 px-3 py-2.5 text-gray-500 hover:bg-red-50 hover:text-red-600 rounded-lg transition text-sm"
              >
                <X className="w-5 h-5" />
                <span>Sign Out</span>
              </button>
            </div>
          </div>
        )}
      </div>
      {previewAsRenter && currentUser?.type === 'landlord' && (
        <div className="bg-gradient-to-r from-amber-50 to-orange-50 border-b border-amber-200 text-amber-700 text-xs py-1.5 text-center font-medium" role="status" aria-live="polite">
          <span className="inline-flex items-center gap-1.5">
            <span className="w-2 h-2 bg-amber-400 rounded-full animate-pulse"></span>
            Preview Mode: Viewing as a renter. Toggle off to manage your listings.
          </span>
        </div>
      )}
    </header>
  );
}
