import React, { useState, useEffect } from 'react';
import { Search, PlusCircle, Home, User, MapPin } from 'lucide-react';
import ListingCard from './ListingCard';
import MapView from '../MapView';
import { SidebarAd, InFeedAd } from './AdBanner';

export default function BrowseView({
  listings,
  searchLocation,
  setSearchLocation,
  priceRange,
  setPriceRange,
  selectedAmenities,
  setSelectedAmenities,
  sortBy,
  setSortBy,
  mapView,
  setMapView,
  onSelectListing,
  setCurrentView,
  currentUser,
  onRequireAuth,
  subscribeToArea,
  onFindNearby,
  nearbyRadius,
  setNearbyRadius,
  nearbyMode,
  clearNearby,
  userLocation,
  nearbyStatus,
  previewMode
}) {
  const [showAdvancedFilters, setShowAdvancedFilters] = useState(false);
  const [isDesktop, setIsDesktop] = useState(typeof window !== 'undefined' ? window.innerWidth >= 1024 : false);

  useEffect(() => {
    const handleResize = () => setIsDesktop(window.innerWidth >= 1024);
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Sidebar navigation items (desktop only)
  const desktopNavItems = [
    { id: 'browse', label: 'Browse', icon: Search },
    { id: 'add', label: 'List Room', icon: PlusCircle, show: currentUser && currentUser.type === 'landlord' && !previewMode },
    { id: 'my-listings', label: 'My Rooms', icon: Home, show: currentUser && currentUser.type === 'landlord' && !previewMode },
    { id: 'profile', label: 'Profile', icon: User }
  ];

  const amenityOptions = ['WiFi', 'Parking', 'Kitchen', 'Laundry', 'Air Conditioning', 'Heating', 'Furnished'];

  const toggleAmenity = (amenity) => {
    if (selectedAmenities.includes(amenity)) {
      setSelectedAmenities(selectedAmenities.filter(a => a !== amenity));
    } else {
      setSelectedAmenities([...selectedAmenities, amenity]);
    }
  };

  const clearChip = (type, value) => {
    if (type === 'location') setSearchLocation('');
    if (type === 'priceMin') setPriceRange([0, priceRange[1]]);
    if (type === 'priceMax') setPriceRange([priceRange[0], 10000]);
    if (type === 'amenity') setSelectedAmenities(selectedAmenities.filter(a => a !== value));
    if (type === 'sort') setSortBy('newest');
    if (type === 'nearby') clearNearby();
  };

  const renderFilterChips = () => {
    const chips = [];
    if (searchLocation) chips.push({ type: 'location', label: searchLocation });
    if (priceRange[0] > 0) chips.push({ type: 'priceMin', label: `Min R${priceRange[0]}` });
    if (priceRange[1] < 10000) chips.push({ type: 'priceMax', label: `Max R${priceRange[1]}` });
    selectedAmenities.forEach(a => chips.push({ type: 'amenity', label: a, value: a }));
    if (sortBy !== 'newest') chips.push({ type: 'sort', label: `Sort: ${sortBy}` });
    if (nearbyMode && userLocation) chips.push({ type: 'nearby', label: `Within ${nearbyRadius}km` });
    if (chips.length === 0) return null;
    return (
      <div className="filter-chip-bar" aria-label="Active filters">
        {chips.map((chip, idx) => (
          <button
            key={idx}
            onClick={() => clearChip(chip.type, chip.value)}
            className="filter-chip"
            aria-label={`Remove ${chip.label} filter`}
          >
            <span>{chip.label}</span>
            <span aria-hidden="true">✕</span>
          </button>
        ))}
        <button
          onClick={() => {
            setSearchLocation('');
            setPriceRange([0, 10000]);
            setSelectedAmenities([]);
            setSortBy('newest');
            if (nearbyMode) clearNearby();
          }}
          className="filter-chip bg-red-50 text-red-700 hover:bg-red-100"
        >Clear All</button>
      </div>
    );
  };

  const renderFiltersBox = () => (
    <div className="filters-box" aria-label="Filters panel">
      <div className="flex items-center gap-2 mb-2">
        <MapPin className="w-4 h-4 text-gray-400" />
        <input
          type="text"
          placeholder="Search location..."
          value={searchLocation}
          onChange={(e) => setSearchLocation(e.target.value)}
          className="flex-1 px-3 py-2 border border-gray-300 rounded-md text-sm focus:ring-2 focus:ring-teal-600 focus:border-transparent"
          aria-label="Location search"
        />
      </div>
      <div className="flex flex-col gap-3">
        <div className="flex flex-col gap-1">
          <label className="text-xs font-semibold text-gray-600">Price Range</label>
          <div className="flex items-center gap-2 text-xs bg-gray-50 px-2 py-1 rounded border border-gray-200">
            <span>R{priceRange[0].toLocaleString()} - R{priceRange[1].toLocaleString()}</span>
            <select
              value={priceRange[0]}
              onChange={(e) => setPriceRange([parseInt(e.target.value), priceRange[1]])}
              className="bg-transparent border-l border-gray-300 pl-2 focus:outline-none"
              aria-label="Minimum price"
            >
              <option value="0">Min</option>
              <option value="500">R500</option>
              <option value="1000">R1000</option>
              <option value="1500">R1500</option>
            </select>
            <select
              value={priceRange[1]}
              onChange={(e) => setPriceRange([priceRange[0], parseInt(e.target.value)])}
              className="bg-transparent border-l border-gray-300 pl-2 focus:outline-none"
              aria-label="Maximum price"
            >
              <option value="10000">R10,000</option>
              <option value="5000">R5,000</option>
              <option value="3000">R3,000</option>
              <option value="2000">R2,000</option>
            </select>
          </div>
        </div>
        <div className="flex flex-col gap-1">
          <label className="text-xs font-semibold text-gray-600">Sort</label>
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="text-sm bg-white border border-gray-300 px-2 py-1 rounded focus:ring-2 focus:ring-teal-600"
            aria-label="Sort order"
          >
            <option value="newest">Newest</option>
            <option value="cheapest">Cheapest</option>
            <option value="expensive">Most Expensive</option>
          </select>
        </div>
        <div>
          <label className="text-xs font-semibold text-gray-600 mb-1 block">Amenities</label>
          <div className="flex flex-wrap gap-1">
            {amenityOptions.map(a => (
              <button
                key={a}
                type="button"
                onClick={() => toggleAmenity(a)}
                className={`px-2 py-1 rounded-md text-[11px] font-medium border transition ${selectedAmenities.includes(a) ? 'bg-teal-600 text-white border-teal-600 shadow-sm' : 'bg-gray-100 text-gray-700 border-gray-300 hover:bg-gray-200'}`}
                aria-pressed={selectedAmenities.includes(a)}
              >{a}</button>
            ))}
          </div>
        </div>
        <div className="mt-2 flex flex-col gap-2">
          <label className="text-xs font-semibold text-gray-600">Nearby Search</label>
          <div className="flex gap-2">
            <button
              type="button"
              onClick={() => onFindNearby && onFindNearby()}
              className="flex-1 bg-green-600 hover:bg-green-700 text-white px-2 py-1 rounded text-xs font-medium"
            >Find Nearby</button>
            <select
              value={nearbyRadius}
              onChange={(e) => setNearbyRadius(parseInt(e.target.value))}
              className="text-xs bg-gray-50 border border-gray-300 px-2 py-1 rounded"
              aria-label="Nearby radius"
            >
              <option value={1}>1km</option>
              <option value={3}>3km</option>
              <option value={5}>5km</option>
              <option value={10}>10km</option>
            </select>
          </div>
          {nearbyMode && (
            <button
              onClick={clearNearby}
              className="text-xs bg-white border border-gray-300 text-gray-700 px-2 py-1 rounded"
            >Clear Nearby</button>
          )}
          {nearbyStatus && <div className="text-xs text-gray-500" aria-live="polite">{nearbyStatus}</div>}
        </div>
        <button
          onClick={() => setShowAdvancedFilters(!showAdvancedFilters)}
          className="mt-2 text-xs text-teal-700 hover:text-teal-800 font-semibold px-2 py-1 rounded hover:bg-teal-50 transition"
          aria-expanded={showAdvancedFilters}
        >{showAdvancedFilters ? 'Close Advanced' : 'More Filters'}</button>
        {showAdvancedFilters && (
          <div className="mt-2 p-2 bg-gray-50 border border-gray-200 rounded" aria-label="Advanced filters"><p className="text-xs text-gray-600">(Advanced filters placeholder)</p></div>
        )}
      </div>
    </div>
  );

  // Desktop layout
  if (isDesktop) {
    return (
      <div className="bg-gray-50 min-h-screen pb-4" aria-labelledby="results-heading">
        <div className="layout-desktop">
          <aside className="sidebar-nav" role="navigation" aria-label="Desktop navigation and filters">
            <div>
              <h2 className="text-xl font-bold flex items-center gap-2 mb-4"><span className="text-teal-600">Room</span><span className="text-red-500">24</span></h2>
              <div className="sidebar-section-title mb-2">Navigation</div>
              <div className="sidebar-menu">
                {desktopNavItems.filter(i => i.show === undefined || i.show).map(item => {
                  const Icon = item.icon;
                  const isActive = item.id === 'browse';
                  return (
                    <button
                      key={item.id}
                      onClick={() => setCurrentView(item.id)}
                      className={isActive ? 'active' : ''}
                      aria-label={item.label}
                    >
                      <Icon className="w-4 h-4" /> {item.label}
                    </button>
                  );
                })}
              </div>
            </div>
            <div>
              <div className="sidebar-section-title mb-2">Filters</div>
              {renderFiltersBox()}
            </div>
            {/* Sidebar Ad */}
            <SidebarAd />
            {!currentUser && (
              <div className="mt-auto pt-4 border-t border-gray-200">
                <p className="text-xs text-gray-500 mb-2">Join to list rooms.</p>
                <div className="flex gap-2">
                  <button onClick={() => onRequireAuth && onRequireAuth('renter')} className="flex-1 bg-gray-100 hover:bg-gray-200 text-gray-800 text-xs py-2 rounded">Sign In</button>
                  <button onClick={() => onRequireAuth && onRequireAuth('landlord')} className="flex-1 bg-red-500 hover:bg-red-600 text-white text-xs py-2 rounded">List Room</button>
                </div>
              </div>
            )}
          </aside>
          <main className="desktop-split" role="main">
            <div className="list-pane">
              <div className="flex items-center justify-between mb-3">
                <h2 id="results-heading" className="text-lg font-semibold text-gray-800">{listings.length === 0 ? 'No rooms' : `${listings.length} rooms`}</h2>
                <button
                  onClick={() => setMapView(!mapView)}
                  className="text-xs px-3 py-1.5 rounded border border-gray-300 bg-white hover:bg-gray-50 transition"
                  aria-pressed={mapView}
                >{mapView ? 'Hide Map' : 'Show Map'}</button>
              </div>
              {renderFilterChips()}
              {listings.length === 0 ? (
                <div className="bg-white border border-gray-200 rounded-lg p-8 text-center"><p className="text-gray-600 text-sm">No rooms match your filters.</p></div>
              ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 2xl:grid-cols-4 gap-6">
                  {listings.map((listing, idx) => (
                    <React.Fragment key={listing.id}>
                      <ListingCard listing={listing} onClick={() => onSelectListing(listing)} />
                      {/* Insert in-feed ad after every 6th listing */}
                      {(idx + 1) % 6 === 0 && idx !== listings.length - 1 && (
                        <div className="md:col-span-2 xl:col-span-3 2xl:col-span-4">
                          <InFeedAd />
                        </div>
                      )}
                    </React.Fragment>
                  ))}
                </div>
              )}
            </div>
            {mapView && (
              <div className="map-pane">
                <MapView
                  listings={listings}
                  onMarkerClick={(l) => onSelectListing(l)}
                  userLocation={userLocation}
                  nearbyRadius={nearbyRadius}
                  fullHeight={true}
                  onRadiusChange={setNearbyRadius}
                  onRecenter={() => {/* handled in MapView */}}
                />
              </div>
            )}
          </main>
        </div>
      </div>
    );
  }

  // Mobile / tablet layout
  return (
    <div className="bg-gray-50 min-h-screen pb-20" role="main" aria-labelledby="results-heading">
      <div className="bg-white border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 py-6">
          <div className="mb-4 flex gap-3">
            <div className="flex-1 relative">
              <MapPin className="absolute left-3 top-3.5 w-5 h-5 text-gray-400" />
              <input
                type="text"
                placeholder="Search by location, suburb, or area..."
                value={searchLocation}
                onChange={(e) => setSearchLocation(e.target.value)}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg text-gray-700 focus:ring-2 focus:ring-teal-600 focus:border-transparent shadow-sm"
                aria-label="Location search"
              />
            </div>
            <div className="flex items-center gap-2">
              <button className="bg-teal-600 hover:bg-teal-700 text-white px-5 py-3 rounded-lg transition font-semibold shadow-sm" onClick={() => {}} aria-label="Search submit">Search</button>
              <button
                onClick={() => {
                  if (!searchLocation || searchLocation.trim() === '') {
                    alert('Enter a location to subscribe.');
                    return;
                  }
                  if (!currentUser) {
                    onRequireAuth && onRequireAuth('renter');
                    return;
                  }
                  subscribeToArea && subscribeToArea(currentUser.id, searchLocation.trim());
                }}
                className="bg-white border border-gray-200 text-gray-700 px-4 py-2 rounded-lg hover:bg-gray-50 transition text-sm"
              >Subscribe</button>
              <button
                type="button"
                onClick={() => onFindNearby && onFindNearby()}
                className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg text-sm font-medium transition"
                aria-label="Find nearby"
              >Nearby</button>
              <select value={nearbyRadius} onChange={(e) => setNearbyRadius(parseInt(e.target.value))} className="text-sm bg-gray-50 border border-gray-200 px-3 py-2 rounded-lg text-gray-700" aria-label="Nearby radius">
                <option value={1}>1 km</option>
                <option value={3}>3 km</option>
                <option value={5}>5 km</option>
                <option value={10}>10 km</option>
              </select>
              {nearbyMode && (
                <button onClick={clearNearby} className="bg-white border border-gray-200 text-gray-700 px-3 py-2 rounded-lg text-sm" aria-label="Clear nearby">Clear</button>
              )}
            </div>
          </div>
          <div className="flex gap-2 mb-3 flex-wrap items-center">
            <div className="flex items-center gap-2 bg-gray-50 px-3 py-2 rounded-lg border border-gray-200">
              <span className="text-sm text-gray-600 font-medium">R{priceRange[0].toLocaleString()} - R{priceRange[1].toLocaleString()}</span>
              <select
                value={priceRange[0]}
                onChange={(e) => setPriceRange([parseInt(e.target.value), priceRange[1]])}
                className="text-xs bg-transparent border-l border-gray-300 pl-2 text-gray-600 focus:outline-none"
                aria-label="Minimum price"
              >
                <option value="0">Min</option>
                <option value="500">R500</option>
                <option value="1000">R1000</option>
                <option value="1500">R1500</option>
              </select>
              <select
                value={priceRange[1]}
                onChange={(e) => setPriceRange([priceRange[0], parseInt(e.target.value)])}
                className="text-xs bg-transparent border-l border-gray-300 pl-2 text-gray-600 focus:outline-none"
                aria-label="Maximum price"
              >
                <option value="10000">R10k</option>
                <option value="5000">R5k</option>
                <option value="3000">R3k</option>
                <option value="2000">R2k</option>
              </select>
            </div>
            <div className="flex items-center gap-2">
              <span className="text-sm text-gray-600">Sort:</span>
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="text-sm bg-gray-50 border border-gray-200 px-3 py-2 rounded-lg text-gray-700 focus:ring-2 focus:ring-teal-600"
                aria-label="Sort order"
              >
                <option value="newest">Newest</option>
                <option value="cheapest">Cheapest</option>
                <option value="expensive">Most Expensive</option>
              </select>
            </div>
            <button
              onClick={() => setShowAdvancedFilters(!showAdvancedFilters)}
              className="text-sm text-teal-700 hover:text-teal-800 font-medium px-3 py-2 rounded-lg hover:bg-teal-50 transition"
              aria-expanded={showAdvancedFilters}
            >{showAdvancedFilters ? '✕ Filters' : '⚙️ Filters'}</button>
          </div>
          {showAdvancedFilters && (
            <div className="mt-3 p-4 bg-gray-50 rounded-lg border border-gray-200">
              <div className="flex flex-wrap gap-1 mb-2" aria-label="Amenities">
                {amenityOptions.map(a => (
                  <button
                    key={a}
                    type="button"
                    onClick={() => toggleAmenity(a)}
                    className={`px-2 py-1 rounded-md text-[11px] font-medium border transition ${selectedAmenities.includes(a) ? 'bg-teal-600 text-white border-teal-600 shadow-sm' : 'bg-gray-100 text-gray-700 border-gray-300 hover:bg-gray-200'}`}
                    aria-pressed={selectedAmenities.includes(a)}
                  >{a}</button>
                ))}
              </div>
              <p className="text-xs text-gray-500">Select amenities to refine results.</p>
            </div>
          )}
          {renderFilterChips()}
        </div>
      </div>
      {/* Mobile Banner Ad */}
      <div className="max-w-7xl mx-auto px-4 pt-3">
        <InFeedAd />
      </div>
      <div className="max-w-7xl mx-auto px-4">
        <div className="flex justify-between items-center mt-4 mb-3">
          <h2 id="results-heading" className="text-2xl font-bold text-gray-800">{listings.length === 0 ? 'No rooms available' : `${listings.length} Rooms Available`}</h2>
          <button
            onClick={() => setMapView(!mapView)}
            className="text-sm px-4 py-2 rounded-lg border border-gray-200 bg-white hover:bg-gray-50 transition flex items-center gap-2"
            aria-pressed={mapView}
          >
            <MapPin className="w-4 h-4" /> {mapView ? 'Show List' : 'Show Map'}
          </button>
        </div>
        {mapView && (
          <div className="mb-6">
            <MapView
              listings={listings}
              onMarkerClick={(l) => onSelectListing(l)}
              userLocation={userLocation}
              nearbyRadius={nearbyRadius}
              fullHeight={false}
              onRadiusChange={setNearbyRadius}
            />
          </div>
        )}
        {listings.length === 0 ? (
          <div className="bg-white rounded-lg border border-gray-200 p-12 text-center">
            <Home className="w-16 h-16 mx-auto mb-4 text-gray-300" />
            <p className="text-gray-600 font-medium mb-2">No rooms match your search</p>
            <p className="text-gray-500 text-sm mb-4">Try adjusting your filters or search in a different location</p>
            <button
              onClick={() => {
                setSearchLocation('');
                setPriceRange([0, 10000]);
                setSelectedAmenities([]);
                setSortBy('newest');
              }}
              className="text-teal-700 hover:text-teal-800 font-medium text-sm"
            >Clear all filters</button>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 mb-12">
            {listings.map((listing, idx) => (
              <React.Fragment key={listing.id}>
                <ListingCard listing={listing} onClick={() => onSelectListing(listing)} />
                {/* Insert in-feed ad after every 8th listing on mobile */}
                {(idx + 1) % 8 === 0 && idx !== listings.length - 1 && (
                  <div className="sm:col-span-2 lg:col-span-3 xl:col-span-4">
                    <InFeedAd />
                  </div>
                )}
              </React.Fragment>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
