function FavoritesView({ listings, onSelectListing }) {
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    try {
      const rawFavorites = localStorage.getItem('favorites');
      if (rawFavorites) {
        const arr = JSON.parse(rawFavorites);
        if (Array.isArray(arr)) setFavorites(arr);
      }
    } catch {}
  }, []);

  const toggleFavorite = (listingId) => {
    setFavorites(prev => {
      const updated = prev.filter(id => id !== listingId);
      try {
        localStorage.setItem('favorites', JSON.stringify(updated));
      } catch {}
      return updated;
    });
  };

  const favoriteListings = listings.filter(l => favorites.includes(l.id));

  return (
    <div className="p-4 min-h-screen bg-gray-50 pb-20">
      <div className="max-w-7xl mx-auto mt-8">
        <div className="mb-6">
          <h2 className="text-3xl font-bold text-gray-800 flex items-center gap-3">
            <Heart className="w-8 h-8 text-red-500 fill-red-500" />
            Saved Listings
          </h2>
          <p className="text-gray-600 mt-2">
            {favoriteListings.length === 0
              ? 'No saved listings yet. Browse and tap the heart icon to save your favorites!'
              : `${favoriteListings.length} saved ${favoriteListings.length === 1 ? 'listing' : 'listings'}`}
          </p>
        </div>

        {favoriteListings.length === 0 ? (
          <div className="bg-white rounded-lg border border-gray-200 p-12 text-center">
            <Heart className="w-24 h-24 mx-auto mb-4 text-gray-300" />
            <p className="text-gray-600 font-medium mb-2">No saved listings</p>
            <p className="text-gray-500 text-sm mb-6">
              Start browsing and save your favorite rooms to find them here later
            </p>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {favoriteListings.map(listing => {
              const ListingCard = require('./components/ListingCard').default;
              return (
                <ListingCard
                  key={listing.id}
                  listing={listing}
                  onClick={() => onSelectListing(listing)}
                  isFavorite={true}
                  onToggleFavorite={toggleFavorite}
                />
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
}

function AddListingView({ onSubmit, onCancel, currentUser, userType, onRequireAuth, onRequireOnboarding }) {
  const GEOCODER_KEY = process.env.REACT_APP_GEOCODER_API_KEY;
  const GEOCODER_PROVIDER = (process.env.REACT_APP_GEOCODER_PROVIDER || '').toLowerCase();

  const [formData, setFormData] = useState({
    title: '',
    price: '',
    location: '',
    streetAddress: '',
    description: '',
    photos: [],
    status: 'available',
    availableDate: new Date().toISOString().split('T')[0],
    amenities: [],
    latitude: null,
    longitude: null,
    paymentMethod: 'Bank and Cash',
    roomType: 'private',
    leaseDuration: '7-12',
    petFriendly: false,
    genderPreference: 'any'
  });
  const [geocodingStatus, setGecodingStatus] = useState('');
  const [errors, setErrors] = useState({});
  const [locationHelpBanner, setLocationHelpBanner] = useState('');
  const [showPhotoEditor, setShowPhotoEditor] = useState(false);
  const reverseGeocodeCacheRef = React.useRef({});
  const [lastGeocodeSource, setLastGeocodeSource] = useState(null); // 'cached' | 'mapbox' | 'proxy' | 'direct' | 'coords-only' | 'manual-geocode'

  // Helper: fetch with graceful fallbacks when third-party APIs lack CORS headers
  const corsFallbackTransforms = useMemo(() => [
    (url) => url.startsWith('https://cors.isomorphic-git.org/') ? url : `https://cors.isomorphic-git.org/${url}`,
    (url) => url.startsWith('https://corsproxy.io/?') ? url : `https://corsproxy.io/?${encodeURIComponent(url)}`
  ], []);

  const fetchWithCorsFallback = useCallback(async (url, options = {}) => {
    const cloneOptions = () => {
      const base = { ...options };
      if (options.headers) base.headers = { ...options.headers };
      return base;
    };

    const attempt = async (targetUrl) => {
      const resp = await fetch(targetUrl, cloneOptions());
      if (!resp.ok) throw new Error(`Request failed with status ${resp.status}`);
      return resp;
    };

    try {
      return await attempt(url);
    } catch (err) {
      if (options.signal?.aborted) throw err;
      let lastError = err;
      for (const transform of corsFallbackTransforms) {
        try {
          const proxiedUrl = transform(url);
          return await attempt(proxiedUrl);
        } catch (proxyErr) {
          lastError = proxyErr;
          if (options.signal?.aborted) break;
        }
      }
      throw lastError;
    }
  }, [corsFallbackTransforms]);

  // Reusable reverse geocoding function - extracted for use by both GPS and draggable marker
  const reverseGeocodeCoords = async (lat, lon) => {
    const cacheKey = `${lat.toFixed(4)},${lon.toFixed(4)}`;

    // Check cache first
    const cached = reverseGeocodeCacheRef.current[cacheKey];
    if (cached && cached.streetAddress && cached.location) {
      return { streetAddress: cached.streetAddress, location: cached.location, source: 'cached' };
    }

    // Try geocoding
    try {
      // Try Mapbox if available
      if (GEOCODER_PROVIDER === 'mapbox' && GEOCODER_KEY) {
        const rev = await fetch(`https://api.mapbox.com/geocoding/v5/mapbox.places/${lon},${lat}.json?access_token=${GEOCODER_KEY}&limit=1&country=za&types=address,poi`);
        const revData = await rev.json();
        if (revData?.features?.length > 0) {
          const feature = revData.features[0];
          const address = feature.address || '';
          const streetName = feature.text || '';
          const fullStreet = address ? `${address} ${streetName}` : streetName;

          let area = '';
          if (feature.context) {
            const place = feature.context.find(c => c.id.startsWith('place'));
            const locality = feature.context.find(c => c.id.startsWith('locality'));
            const region = feature.context.find(c => c.id.startsWith('region'));
            const baseArea = (place ? place.text : (locality ? locality.text : '')) || '';
            const province = region ? region.text : '';
            area = province && baseArea && province.toLowerCase() !== baseArea.toLowerCase() ? `${baseArea}, ${province}` : (baseArea || province);
          }
          area = area.replace(/ (Metropolitan|Local|District) Municipality$/i, '').replace(/^City of /i, '').trim();

          if (fullStreet || area) {
            reverseGeocodeCacheRef.current[cacheKey] = { streetAddress: fullStreet, location: area, ts: Date.now() };
            try { localStorage.setItem('reverse-geocode-cache', JSON.stringify(reverseGeocodeCacheRef.current)); } catch {}
            return { streetAddress: fullStreet, location: area, source: 'mapbox' };
          }
        }
      }

      // Try geocode.maps.co (Nominatim mirror with CORS enabled)
      try {
        const mapsCoResp = await fetch(`https://geocode.maps.co/reverse?lat=${lat}&lon=${lon}`);
        if (mapsCoResp.ok) {
          const mapsCoData = await mapsCoResp.json();
          if (mapsCoData?.address) {
            const addr = mapsCoData.address;
            const houseNumber = addr.house_number || '';
            const road = addr.road || addr.street || '';
            const suburb = addr.suburb || addr.neighbourhood || '';
            const street = [houseNumber, road].filter(Boolean).join(' ').trim() || suburb;
            const area = formatAreaFromAddress(addr) || suburb;
            if (street || area) {
              reverseGeocodeCacheRef.current[cacheKey] = { streetAddress: street, location: area, ts: Date.now() };
              try { localStorage.setItem('reverse-geocode-cache', JSON.stringify(reverseGeocodeCacheRef.current)); } catch {}
              return { streetAddress: street, location: area, source: 'maps.co' };
            }
          }
        }
      } catch (mapsCoErr) {
        console.warn('maps.co reverse geocode failed', mapsCoErr);
      }

      // Try BigDataCloud (CORS-friendly, no key required)
      try {
        const bdcResp = await fetch(`https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${lat}&longitude=${lon}&localityLanguage=en`);
        if (bdcResp.ok) {
          const bdcData = await bdcResp.json();
          const formatted = formatBigDataCloudAddress(bdcData);
          if (formatted.streetAddress || formatted.location) {
            reverseGeocodeCacheRef.current[cacheKey] = { streetAddress: formatted.streetAddress, location: formatted.location, ts: Date.now() };
            try { localStorage.setItem('reverse-geocode-cache', JSON.stringify(reverseGeocodeCacheRef.current)); } catch {}
            return { ...formatted, source: 'bigdatacloud' };
          }
        }
      } catch (bdcErr) {
        console.warn('BigDataCloud reverse geocode failed', bdcErr);
      }

      // Try proxy/Nominatim
      const API_BASE = (process.env.REACT_APP_PROXY_BASE || (window.location && window.location.port === '8443' ? 'https://localhost:4443' : ''));
      let revData = null;

      try {
        const rev = await fetch(`${API_BASE}/api/reverse?lat=${lat}&lon=${lon}`);
        if (rev.ok) revData = await rev.json();
      } catch (proxyErr) {
        console.warn('Proxy reverse geocode failed, trying direct', proxyErr);
      }

      // Direct Nominatim fallback
      if (!revData) {
        try {
          const direct = await fetchWithCorsFallback(
            `https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lon}&zoom=18&addressdetails=1`,
            { headers: { 'Accept': 'application/json', 'User-Agent': 'room-rental-platform/1.0' } }
          );
          revData = await direct.json();
        } catch (directErr) {
          console.warn('Direct nominatim failed', directErr);
        }
      }

      if (revData?.address) {
        const addr = revData.address;
        const houseNumber = addr.house_number || '';
        const road = addr.road || addr.street || '';
        const suburb = addr.suburb || addr.neighbourhood || '';
        const street = [houseNumber, road].filter(Boolean).join(' ').trim() || suburb;
        const area = formatAreaFromAddress(addr) || suburb;

        if (street || area) {
          reverseGeocodeCacheRef.current[cacheKey] = { streetAddress: street, location: area, ts: Date.now() };
          try { localStorage.setItem('reverse-geocode-cache', JSON.stringify(reverseGeocodeCacheRef.current)); } catch {}
          return { streetAddress: street, location: area, source: 'nominatim' };
        }
      }
    } catch (e) {
      console.error('Reverse geocode error:', e);
    }

    return null; // Failed to geocode
  };

  // Forward geocode helper for manual address lookups
  const forwardGeocodeAddress = async (query) => {
    if (!query || query.trim().length < 3) return [];

    // 1. Mapbox (if configured)
    if (GEOCODER_PROVIDER === 'mapbox' && GEOCODER_KEY) {
      try {
        const resp = await fetch(`https://api.mapbox.com/geocoding/v5/mapbox.places/${encodeURIComponent(query)}.json?access_token=${GEOCODER_KEY}&limit=3&country=za&types=address,place,locality`);
        const data = await resp.json();
        if (data?.features?.length) {
          return data.features.map(f => ({
            lat: f.center ? f.center[1] : f.geometry?.coordinates?.[1],
            lon: f.center ? f.center[0] : f.geometry?.coordinates?.[0],
            label: f.place_name || f.text,
            source: 'mapbox'
          })).filter(entry => typeof entry.lat === 'number' && typeof entry.lon === 'number');
        }
      } catch (err) {
        console.warn('Mapbox forward geocode failed', err);
      }
    }

    // 2. maps.co (CORS-friendly)
    try {
      const mapsResp = await fetch(`https://geocode.maps.co/search?q=${encodeURIComponent(query)}&limit=3`);
      if (mapsResp.ok) {
        const mapsData = await mapsResp.json();
        if (Array.isArray(mapsData) && mapsData.length) {
          return mapsData.slice(0, 3).map(entry => ({
            lat: parseFloat(entry.lat),
            lon: parseFloat(entry.lon),
            label: entry.display_name || entry.name,
            source: 'maps.co'
          }));
        }
      }
    } catch (mapsErr) {
      console.warn('maps.co forward geocode failed', mapsErr);
    }

    // 3. Local proxy (if running)
    const API_BASE = (process.env.REACT_APP_PROXY_BASE || (window.location && window.location.port === '8443' ? 'https://localhost:4443' : ''));
    if (API_BASE) {
      try {
        const proxyResp = await fetch(`${API_BASE}/api/search?q=${encodeURIComponent(query)}`);
        if (proxyResp.ok) {
          const proxyData = await proxyResp.json();
          if (Array.isArray(proxyData) && proxyData.length) {
            return proxyData.slice(0, 3).map(entry => ({
              lat: parseFloat(entry.lat),
              lon: parseFloat(entry.lon),
              label: entry.display_name,
              source: API_BASE.includes('localhost') ? 'local-proxy' : 'proxy'
            }));
          }
        }
      } catch (proxyErr) {
        console.warn('Local proxy forward geocode failed', proxyErr);
      }
    }

    // 4. Direct Nominatim fallback (may still be blocked, but last resort)
    try {
      const directResp = await fetchWithCorsFallback(`https://nominatim.openstreetmap.org/search?format=json&addressdetails=1&q=${encodeURIComponent(query)}`, {
        headers: {
          'User-Agent': 'room-rental-platform/1.0 (direct fallback)',
          'Accept': 'application/json'
        }
      });
      if (directResp.ok) {
        const directData = await directResp.json();
        if (Array.isArray(directData) && directData.length) {
          return directData.slice(0, 3).map(entry => ({
            lat: parseFloat(entry.lat),
            lon: parseFloat(entry.lon),
            label: entry.display_name,
            source: 'direct'
          }));
        }
      }
    } catch (directErr) {
      console.warn('Direct nominatim forward geocode failed', directErr);
    }

    return [];
  };

  const availableAmenities = [
