  // Geocode address using Nominatim (OpenStreetMap)
  // Use browser geolocation and reverse-geocode to fill address/coords
  // Helper: format city + province (South Africa) removing verbose municipality suffixes
  const formatAreaFromAddress = (addr) => {
    if (!addr) return '';
    const cleanMunicipality = (val) => {
      if (!val) return '';
      return val
        .replace(/ City of /i, '')
        .replace(/ (Metropolitan|Local|District) Municipality$/i, '')
        .trim();
    };

    // Prioritize suburb/neighborhood first as it's most specific
    let suburb = addr.suburb || addr.neighbourhood || '';
    let city = addr.city || addr.town || addr.village || addr.hamlet || '';
    let municipality = cleanMunicipality(addr.municipality || '');
    city = cleanMunicipality(city);

    // Use suburb if available, otherwise fall back to city, then municipality
    let primaryArea = suburb || city || municipality;
    const province = addr.state || '';

    // Avoid duplication like "Johannesburg, Johannesburg"
    if (province && primaryArea && province.toLowerCase() === primaryArea.toLowerCase()) {
      return primaryArea;
    }
    if (primaryArea && province) return `${primaryArea}, ${province}`;
    return primaryArea || province || '';
  };

  const formatBigDataCloudAddress = (data) => {
    if (!data) return { streetAddress: '', location: '' };
    const informative = data.localityInfo?.informative || [];
    const admin = data.localityInfo?.administrative || [];

    const streetCandidate = informative.find((entry) => /road|street|route|drive|avenue|lane|close|crescent/i.test(entry.description || entry.name || ''))
      || informative.find((entry) => /residential|locality/i.test(entry.description || ''));

    const suburbCandidate = admin.find((entry) => entry.order === 5)
      || admin.find((entry) => /suburb|township|neighbourhood/i.test(entry.description || ''));

    const cityCandidate = admin.find((entry) => entry.order === 6)
      || admin.find((entry) => /city|town/i.test(entry.description || ''));

    const streetAddress = streetCandidate?.name || '';
    const areaParts = [
      suburbCandidate?.name || data.locality || data.city || '',
      data.principalSubdivision || cityCandidate?.name || ''
    ].filter(Boolean);

    return {
      streetAddress,
      location: areaParts.filter((val, idx, arr) => val && arr.indexOf(val) === idx).join(', ')
    };
  };

  // (Cache ref declared earlier in component; duplicate removed)

  const geolocateCurrentPosition = () => {
    const insecure = (typeof window !== 'undefined') && !window.isSecureContext && window.location.hostname !== 'localhost';
    if (insecure) {
      setGecodingStatus('Location is blocked on HTTP. Use HTTPS to enable.');
      setLocationHelpBanner('Your browser requires HTTPS to access GPS on mobile. Use the HTTPS link below.');
      return;
    }
    if (!navigator.geolocation) {
      setGecodingStatus('Geolocation not supported by your browser');
      setLocationHelpBanner('Location is disabled. Tap for steps to enable.');
      return;
    }

    setGecodingStatus('Getting your precise location...');
    navigator.geolocation.getCurrentPosition(async (pos) => {
      const lat = pos.coords.latitude;
      const lon = pos.coords.longitude;
      const accuracy = pos.coords.accuracy;

      console.log(`Location obtained: ${lat}, ${lon} (accuracy: ${accuracy}m)`);

      try {
        const geocodeResult = await reverseGeocodeCoords(lat, lon);
        if (geocodeResult) {
          setFormData(prev => ({
            ...prev,
            streetAddress: geocodeResult.streetAddress || prev.streetAddress,
            location: geocodeResult.location || prev.location,
            latitude: lat,
            longitude: lon
          }));
          setGecodingStatus(` Location set (${accuracy.toFixed(0)}m accuracy)`);
          setTimeout(() => setGecodingStatus(''), 3000);
          setLastGeocodeSource(geocodeResult.source || 'manual-geocode');
          return;
        }
      } catch (e) {
        console.error('Reverse geocode failed', e);
      }

      // Only use coordinate placeholders if reverse geocoding completely failed
      setFormData(prev => ({
        ...prev,
        latitude: lat,
        longitude: lon
      }));
      setGecodingStatus(` Coordinates captured (${accuracy.toFixed(0)}m accuracy). Add the street or suburb manually while we reconnect to the address service.`);
      setTimeout(() => setGecodingStatus(''), 3000);
      setLastGeocodeSource('coords-only');
    }, (err) => {
      console.error('Geolocation error', err);
      if (err && (err.code === 1)) {
        // PERMISSION_DENIED
        setGecodingStatus('Permission denied. Allow location access to continue.');
        setLocationHelpBanner('Tap the lock icon in the address bar and set Location to Allow, then refresh.');
      } else if (err && (err.code === 2)) {
        // POSITION_UNAVAILABLE
        setGecodingStatus('Position unavailable. Check GPS and network, then retry.');
      } else if (err && (err.code === 3)) {
        // TIMEOUT
        setGecodingStatus('Location timed out. Move to open area or retry.');
      } else {
        setGecodingStatus('Could not get your location - ' + (err?.message || 'Unknown error'));
      }
      setLocationHelpBanner('Location permission denied. Tap here for steps to enable.');
      setLastGeocodeSource(null);
    }, { 
      enableHighAccuracy: true, 
      timeout: 15000, 
      maximumAge: 0 
    });
  };

  const geocodeAddress = async () => {
    if (!formData.streetAddress) {
      setGecodingStatus('Please enter a street address');
      return;
    }

    setGecodingStatus('Finding coordinates...');
    try {
      const fullAddress = `${formData.streetAddress}, ${formData.location}, South Africa`;
      const results = await forwardGeocodeAddress(fullAddress);

      if (results.length > 0) {
        const best = results[0];
        setFormData({ 
          ...formData, 
          latitude: parseFloat(best.lat), 
          longitude: parseFloat(best.lon) 
        });
        setGecodingStatus(` Address found!`);
        setTimeout(() => setGecodingStatus(''), 2000);
        setLastGeocodeSource(best.source || 'manual-geocode');
        return;
      }

      setGecodingStatus('Address not found. Trying with area only...');
      const areaResults = await forwardGeocodeAddress(`${formData.location}, South Africa`);
      if (areaResults.length > 0) {
        const bestArea = areaResults[0];
        setFormData({ 
          ...formData, 
          latitude: parseFloat(bestArea.lat), 
          longitude: parseFloat(bestArea.lon) 
        });
        setGecodingStatus(' Area found!');
        setTimeout(() => setGecodingStatus(''), 2000);
        setLastGeocodeSource(bestArea.source || 'manual-geocode');
      } else {
        setGecodingStatus('Could not find location');
      }
    } catch (error) {
      console.error('Geocoding error:', error);
      setGecodingStatus('Error finding address. Check your connection.');
      setLastGeocodeSource(null);
    }
  };

