      return () => clearTimeout(t);
    }
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [formData.latitude, formData.longitude]);

  const googleMapsQuery = [formData.streetAddress, formData.location, 'South Africa']
    .filter(Boolean)
