function BottomNav({ currentView, setCurrentView, userType }) {
  const navItems = [
    { id: 'browse', label: 'Browse', icon: <Search className="w-5 h-5" /> },
    { id: 'add', label: 'List Room', icon: <PlusCircle className="w-5 h-5" />, requiresLandlord: true },
    { id: 'my-rooms', label: 'My Rooms', icon: <Home className="w-5 h-5" /> },
    { id: 'profile', label: 'Profile', icon: <User className="w-5 h-5" /> }
  ];

  return (
    <div className="fixed bottom-0 left-0 right-0 bg-white border-t border-gray-200 px-4 py-2 z-20 md:hidden">
      <div className="grid grid-cols-4 gap-2">
        {navItems.map(item => {
          const disabled = item.requiresLandlord && userType !== 'landlord';
          const isActive = currentView === item.id;
          return (
            <button
              key={item.id}
              type="button"
              disabled={disabled}
              onClick={() => !disabled && setCurrentView(item.id)}
              className={`flex flex-col items-center gap-1 py-1.5 rounded-xl transition-all ${disabled ? 'opacity-40 cursor-not-allowed' : 'hover:text-blue-600'} ${isActive ? 'text-blue-600 font-semibold bg-blue-50' : 'text-gray-500'}`}
            >
              {item.icon}
              <span className="text-xs">{item.label}</span>
            </button>
          );
        })}
      </div>
    </div>
  );
}
