import React from 'react';

export default function MapView({ listings = [], onMarkerClick = () => {} }) {
  return (
    <div className="w-full h-96 bg-gray-100 rounded-lg border border-gray-200 p-4">
      {listings.length === 0 ? (
        <div className="h-full flex items-center justify-center text-gray-500">No listings to show on the map.</div>
      ) : (
        <div className="h-full grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-3 overflow-auto">
          {listings.map(l => (
            <button
              key={l.id}
              onClick={() => onMarkerClick(l)}
              className="text-left bg-white p-3 rounded shadow hover:shadow-md transition"
            >
              <div className="font-semibold text-gray-800">{l.title}</div>
              <div className="text-sm text-gray-500">{l.location}</div>
              <div className="text-red-600 font-bold mt-2">R{Number(l.price).toLocaleString()}/month</div>
            </button>
          ))}
        </div>
      )}
    </div>
  );
}
