function ConversationModal({ conversation, listing, onClose, onSendMessage }) {
  const [message, setMessage] = useState('');

  const handleSend = () => {
    if (message.trim()) {
      onSendMessage(listing.id, message);
      setMessage('');
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] flex flex-col">
        <div className="bg-white border-b p-4 flex items-center">
          <button onClick={onClose} className="mr-3 text-gray-500 hover:text-gray-700">
            <ArrowLeft className="w-6 h-6" />
          </button>
          <div>
            <h2 className="font-bold text-gray-800">{listing?.title}</h2>
            <p className="text-sm text-gray-600">{listing?.location}</p>
          </div>
        </div>

        <div className="flex-1 overflow-y-auto p-4 space-y-3">
          {conversation.messages.map(msg => (
            <div
              key={msg.id}
              className={`flex ${msg.sender === 'landlord' ? 'justify-start' : 'justify-end'}`}
            >
              <div
                className={`max-w-xs px-4 py-2 rounded-lg ${
                  msg.sender === 'landlord'
                    ? 'bg-gray-200 text-gray-800'
                    : 'bg-blue-600 text-white'
                }`}
              >
                <p>{msg.text}</p>
                <span className="text-xs opacity-75">
                  {new Date(msg.timestamp).toLocaleTimeString()}
                </span>
              </div>
            </div>
          ))}
        </div>

        <div className="border-t p-4 flex gap-2">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
            placeholder="Type a message..."
            className="flex-1 px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
          <button
            onClick={handleSend}
            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg transition"
          >
            <Send className="w-5 h-5" />
          </button>
        </div>
      </div>
    </div>
  );
}

function BottomNav({ currentView, setCurrentView, userType, messageCount }) {
  return (
    <div className="fixed bottom-0 left-0 right-0 bg-white border-t shadow-lg">
      <div className="max-w-4xl mx-auto flex justify-around p-2">
        <button
          onClick={() => setCurrentView('browse')}
          className={`flex flex-col items-center p-2 ${
            currentView === 'browse' ? 'text-blue-600' : 'text-gray-600'
          }`}
        >
          <Search className="w-6 h-6" />
          <span className="text-xs mt-1">Browse</span>
        </button>

        <button
          onClick={() => setCurrentView('messages')}
          className={`flex flex-col items-center p-2 relative ${
            currentView === 'messages' ? 'text-blue-600' : 'text-gray-600'
          }`}
        >
          <MessageSquare className="w-6 h-6" />
          {messageCount > 0 && (
            <span className="absolute top-0 right-0 bg-red-500 text-white text-xs rounded-full w-5 h-5 flex items-center justify-center">
              {messageCount}
            </span>
          )}
          <span className="text-xs mt-1">Messages</span>
        </button>

        {userType === 'landlord' && (
          <>
            <button
              onClick={() => setCurrentView('add')}
              className={`flex flex-col items-center p-2 ${
                currentView === 'add' ? 'text-blue-600' : 'text-gray-600'
              }`}
            >
              <PlusCircle className="w-6 h-6" />
              <span className="text-xs mt-1">Post</span>
            </button>

            <button
              onClick={() => setCurrentView('my-listings')}
              className={`flex flex-col items-center p-2 ${
                currentView === 'my-listings' ? 'text-blue-600' : 'text-gray-600'
              }`}
            >
              <Home className="w-6 h-6" />
              <span className="text-xs mt-1">My Rooms</span>
            </button>
          </>
        )}

        <button
          onClick={() => setCurrentView('profile')}
          className={`flex flex-col items-center p-2 ${
            currentView === 'profile' || currentView === 'edit-profile' ? 'text-blue-600' : 'text-gray-600'
          }`}
        >
          <User className="w-6 h-6" />
          <span className="text-xs mt-1">Profile</span>
        </button>
      </div>
    </div>
  );
}
