import React, { useState, useEffect } from 'react';
import { Home, MessageSquare, PlusCircle, Search, MapPin, DollarSign, X, Send, ArrowLeft, User, Phone, Mail, Edit } from 'lucide-react';

export default function RentalPlatform() {
  const [currentView, setCurrentView] = useState('browse');
  const [userType, setUserType] = useState(null);
  const [listings, setListings] = useState([]);
  const [conversations, setConversations] = useState([]);
  const [selectedListing, setSelectedListing] = useState(null);
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [searchLocation, setSearchLocation] = useState('');
  const [priceRange, setPriceRange] = useState([0, 10000]);
  const [selectedAmenities, setSelectedAmenities] = useState([]);
  const [sortBy, setSortBy] = useState('newest');
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      const listingsResult = await window.storage.get('listings');
      const conversationsResult = await window.storage.get('conversations');
      const userResult = await window.storage.get('current-user');

      if (listingsResult) setListings(JSON.parse(listingsResult.value));
      if (conversationsResult) setConversations(JSON.parse(conversationsResult.value));
      if (userResult) {
        const user = JSON.parse(userResult.value);
        setCurrentUser(user);
        setUserType(user.type);
      }
    } catch (error) {
      console.log('No existing data found, starting fresh');
    }
  };

  const saveListings = async (newListings) => {
    try {
      await window.storage.set('listings', JSON.stringify(newListings));
      setListings(newListings);
    } catch (error) {
      console.error('Error saving listings:', error);
    }
  };

  const saveConversations = async (newConversations) => {
    try {
      await window.storage.set('conversations', JSON.stringify(newConversations));
      setConversations(newConversations);
    } catch (error) {
      console.error('Error saving conversations:', error);
    }
  };

  const handleUserTypeSelection = async (type) => {
    setUserType(type);
    setCurrentView('setup-profile');
  };

const handleProfileSetup = async (profileData) => {
  try {
    const userId = `user-${Date.now()}`;
    const user = { 
      id: userId, 
      type: userType, 
      ...profileData,
      createdAt: new Date().toISOString()
    };

    if (window.storage && window.storage.set) {
      await window.storage.set('current-user', JSON.stringify(user));
    }

    setCurrentUser(user);
    setCurrentView('browse');
  } catch (error) {
    console.error('Error saving profile:', error);
    // Still set the user locally even if storage fails
    const userId = `user-${Date.now()}`;
    const user = { 
      id: userId, 
      type: userType, 
      ...profileData,
      createdAt: new Date().toISOString()
    };
    setCurrentUser(user);
    setCurrentView('browse');
  }
};

const handleUpdateProfile = async (profileData) => {
  try {
    const updatedUser = { ...currentUser, ...profileData };

    if (window.storage && window.storage.set) {
      await window.storage.set('current-user', JSON.stringify(updatedUser));
    }

    setCurrentUser(updatedUser);
    setCurrentView('browse');
  } catch (error) {
    console.error('Error updating profile:', error);
    const updatedUser = { ...currentUser, ...profileData };
    setCurrentUser(updatedUser);
    setCurrentView('browse');
  }
};

const handleAddListing = async (listingData) => {
  const newListing = {
    id: `listing-${Date.now()}`,
    title: listingData.title,
    price: listingData.price,
    location: listingData.location,
    description: listingData.description,
    photos: listingData.photos || [],
    status: listingData.status || 'available',
    availableDate: listingData.availableDate || new Date().toISOString(),
    amenities: listingData.amenities || [],
    landlordId: currentUser.id,
    landlordName: currentUser.name,
    landlordPhone: currentUser.phone,
    landlordEmail: currentUser.email,
    landlordPhoto: currentUser.photo,
    createdAt: new Date().toISOString()
  };
    const updatedListings = [...listings, newListing];
    await saveListings(updatedListings);
    setCurrentView('browse');
  };

  const handleSendMessage = async (listingId, message) => {
    const conversationId = `${currentUser.id}-${listingId}`;
    const existingConv = conversations.find(c => c.id === conversationId);

    const newMessage = {
      id: `msg-${Date.now()}`,
      sender: currentUser.type,
      text: message,
      timestamp: new Date().toISOString()
    };

    let updatedConversations;
    if (existingConv) {
      updatedConversations = conversations.map(c => 
        c.id === conversationId 
          ? { ...c, messages: [...c.messages, newMessage] }
          : c
      );
    } else {
      const newConv = {
        id: conversationId,
        listingId,
        renterId: currentUser.id,
        messages: [newMessage]
      };
      updatedConversations = [...conversations, newConv];
    }

    await saveConversations(updatedConversations);
  };

const filteredListings = listings
  .filter(listing => {
    const matchesLocation = !searchLocation || 
      listing.location.toLowerCase().includes(searchLocation.toLowerCase());
    const price = parseFloat(listing.price);
    const matchesPrice = price >= priceRange[0] && price <= priceRange[1];
    const matchesAmenities = selectedAmenities.length === 0 || 
      selectedAmenities.every(amenity => listing.amenities?.includes(amenity));
    return matchesLocation && matchesPrice && matchesAmenities;
  })
  .sort((a, b) => {
    if (sortBy === 'newest') {
      return new Date(b.createdAt) - new Date(a.createdAt);
    } else if (sortBy === 'cheapest') {
      return parseFloat(a.price) - parseFloat(b.price);
    } else if (sortBy === 'expensive') {
      return parseFloat(b.price) - parseFloat(a.price);
    }
    return 0;
  });

  const userConversations = conversations.filter(conv => {
    if (userType === 'landlord') {
      const listing = listings.find(l => l.id === conv.listingId);
      return listing && listing.landlordId === currentUser.id;
    }
    return conv.renterId === currentUser.id;
  });

  if (!userType) {
    return <UserTypeSelector onSelect={handleUserTypeSelection} />;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="bg-blue-600 text-white p-4 shadow-lg">
        <div className="max-w-4xl mx-auto flex items-center justify-between">
          <h1 className="text-xl font-bold">Room Rentals</h1>
          <div className="text-sm bg-blue-700 px-3 py-1 rounded-full">
            {userType === 'landlord' ? 'Landlord' : 'Renter'}
          </div>
        </div>
      </div>

      <div className="max-w-4xl mx-auto pb-20">
        {currentView === 'setup-profile' && (
          <ProfileSetupView onSubmit={handleProfileSetup} userType={userType} />
        )}

        {currentView === 'profile' && currentUser && (
          <ProfileView user={currentUser} onEdit={() => setCurrentView('edit-profile')} />
        )}

        {currentView === 'edit-profile' && currentUser && (
          <EditProfileView user={currentUser} onSubmit={handleUpdateProfile} onCancel={() => setCurrentView('profile')} />
        )}

{currentView === 'browse' && (
  <BrowseView 
    listings={filteredListings}
    searchLocation={searchLocation}
    setSearchLocation={setSearchLocation}
    priceRange={priceRange}
    setPriceRange={setPriceRange}
    selectedAmenities={selectedAmenities}
    setSelectedAmenities={setSelectedAmenities}
    sortBy={sortBy}
    setSortBy={setSortBy}
    onSelectListing={setSelectedListing}
  />
)}

        {currentView === 'messages' && (
          <MessagesView 
            conversations={userConversations}
            listings={listings}
            userType={userType}
            onSelectConversation={setSelectedConversation}
          />
        )}

        {currentView === 'add' && (
          <AddListingView onSubmit={handleAddListing} onCancel={() => setCurrentView('browse')} />
        )}

        {currentView === 'my-listings' && (
          <MyListingsView 
            listings={listings.filter(l => l.landlordId === currentUser.id)}
            onDelete={async (id) => {
              const updated = listings.filter(l => l.id !== id);
              await saveListings(updated);
            }}
          />
        )}
      </div>

      {selectedListing && (
        <ListingDetailModal 
          listing={selectedListing}
          landlord={{
            name: selectedListing.landlordName,
            phone: selectedListing.landlordPhone,
            email: selectedListing.landlordEmail,
            photo: selectedListing.landlordPhoto
          }}
          onClose={() => setSelectedListing(null)}
          onSendMessage={handleSendMessage}
          userType={userType}
        />
      )}

      {selectedConversation && (
        <ConversationModal
          conversation={selectedConversation}
          listing={listings.find(l => l.id === selectedConversation.listingId)}
          onClose={() => setSelectedConversation(null)}
          onSendMessage={handleSendMessage}
        />
      )}

      <BottomNav 
        currentView={currentView}
        setCurrentView={setCurrentView}
        userType={userType}
        messageCount={userConversations.length}
      />
    </div>
  );
}

function UserTypeSelector({ onSelect }) {
  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-500 to-blue-700 flex items-center justify-center p-4">
      <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-md w-full">
        <h2 className="text-3xl font-bold text-center mb-2 text-gray-800">Welcome!</h2>
        <p className="text-gray-600 text-center mb-8">Choose how you want to use the platform</p>

        <div className="space-y-4">
          <button
            onClick={() => onSelect('landlord')}
            className="w-full bg-blue-600 hover:bg-blue-700 text-white p-6 rounded-xl shadow-lg transition flex items-center justify-between group"
          >
            <div className="text-left">
              <div className="text-xl font-bold mb-1">I'm a Landlord</div>
              <div className="text-sm text-blue-100">Post rooms for rent</div>
            </div>
            <Home className="w-8 h-8 group-hover:scale-110 transition" />
          </button>

          <button
            onClick={() => onSelect('renter')}
            className="w-full bg-green-600 hover:bg-green-700 text-white p-6 rounded-xl shadow-lg transition flex items-center justify-between group"
          >
            <div className="text-left">
              <div className="text-xl font-bold mb-1">I'm a Renter</div>
              <div className="text-sm text-green-100">Find rooms to rent</div>
            </div>
            <Search className="w-8 h-8 group-hover:scale-110 transition" />
          </button>
        </div>
      </div>
    </div>
  );
}

function ProfileSetupView({ onSubmit, userType }) {
const [formData, setFormData] = useState({
  title: '',
  price: '',
  location: '',
  description: '',
  photos: [], // Changed from 'photo' to 'photos' array
  status: 'available',
  availableDate: new Date().toISOString().split('T')[0]
});


const handlePhotoUpload = (e) => {
  const files = Array.from(e.target.files);
  if (files.length > 0) {
    const readers = files.map(file => {
      return new Promise((resolve) => {
        const reader = new FileReader();
        reader.onloadend = () => resolve(reader.result);
        reader.readAsDataURL(file);
      });
    });

    Promise.all(readers).then(images => {
      setFormData({ ...formData, photos: [...formData.photos, ...images] });
    });
  }
};

const removePhoto = (index) => {
  const newPhotos = formData.photos.filter((_, i) => i !== index);
  setFormData({ ...formData, photos: newPhotos });
};

  const handleSubmit = () => {
    if (formData.name && formData.phone && formData.email) {
      onSubmit(formData);
    }
  };

  return (
    <div className="p-4 min-h-screen bg-gray-50">
      <div className="bg-white rounded-lg shadow p-6 max-w-xl mx-auto mt-8">
        <h2 className="text-2xl font-bold text-gray-800 mb-2">Complete Your Profile</h2>
        <p className="text-gray-600 mb-6">Help others know who you are</p>

        <div className="space-y-4">
          <div className="flex justify-center mb-6">
            <div className="relative">
              {formData.photo ? (
                <img src={formData.photo} alt="Profile" className="w-32 h-32 rounded-full object-cover border-4 border-blue-500" />
              ) : (
                <div className="w-32 h-32 rounded-full bg-gray-200 flex items-center justify-center border-4 border-gray-300">
                  <User className="w-16 h-16 text-gray-400" />
                </div>
              )}
              <label className="absolute bottom-0 right-0 bg-blue-600 text-white p-2 rounded-full cursor-pointer hover:bg-blue-700 transition">
                <Edit className="w-4 h-4" />
                <input
                  type="file"
                  accept="image/*"
                  onChange={handlePhotoUpload}
                  className="hidden"
                />
              </label>
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Full Name *</label>
            <input
              type="text"
              required
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              placeholder="Enter your full name"
              className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Phone Number *</label>
            <div className="relative">
              <Phone className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
              <input
                type="tel"
                required
                value={formData.phone}
                onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                placeholder="e.g., +27 12 345 6789"
                className="w-full pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Email Address *</label>
            <div className="relative">
              <Mail className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
              <input
                type="email"
                required
                value={formData.email}
                onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                placeholder="your.email@example.com"
                className="w-full pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
              />
            </div>
          </div>

          <button
            onClick={handleSubmit}
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3 rounded-lg transition mt-6"
          >
            Complete Setup
          </button>
        </div>
      </div>
    </div>
  );
}

function ProfileView({ user, onEdit }) {
  return (
    <div className="p-4">
      <div className="bg-white rounded-lg shadow p-6 max-w-xl mx-auto">
        <div className="flex justify-between items-start mb-6">
          <h2 className="text-2xl font-bold text-gray-800">My Profile</h2>
          <button
            onClick={onEdit}
            className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg transition flex items-center gap-2"
          >
            <Edit className="w-4 h-4" />
            Edit
          </button>
        </div>

        <div className="flex flex-col items-center mb-6">
          {user.photo ? (
            <img src={user.photo} alt="Profile" className="w-32 h-32 rounded-full object-cover border-4 border-blue-500 mb-4" />
          ) : (
            <div className="w-32 h-32 rounded-full bg-gray-200 flex items-center justify-center border-4 border-gray-300 mb-4">
              <User className="w-16 h-16 text-gray-400" />
            </div>
          )}
          <h3 className="text-2xl font-bold text-gray-800">{user.name}</h3>
          <span className="inline-block mt-2 px-4 py-1 bg-blue-100 text-blue-700 rounded-full text-sm font-semibold">
            {user.type === 'landlord' ? 'Landlord' : 'Renter'}
          </span>
        </div>

