function BrowseView({ 
  listings, 
  searchLocation, 
  setSearchLocation, 
  priceRange, 
  setPriceRange,
  selectedAmenities,
  setSelectedAmenities,
  sortBy,
  setSortBy,
  onSelectListing 
}) {
  const [showFilters, setShowFilters] = useState(false);

  const availableAmenities = [
    'WiFi', 'Parking', 'Kitchen', 'Laundry', 'Air Conditioning', 
    'Heating', 'TV', 'Furnished', 'Pet Friendly', 'Garden'
  ];

  const toggleAmenity = (amenity) => {
    if (selectedAmenities.includes(amenity)) {
      setSelectedAmenities(selectedAmenities.filter(a => a !== amenity));
    } else {
      setSelectedAmenities([...selectedAmenities, amenity]);
    }
  };

  return (
    <div className="p-4">
      {/* Search and Sort Bar */}
      <div className="bg-white rounded-lg shadow p-4 mb-4">
        <div className="flex items-center justify-between mb-3">
          <h3 className="font-semibold text-gray-700">Search & Filter</h3>
          <button
            onClick={() => setShowFilters(!showFilters)}
            className="text-blue-600 hover:text-blue-700 text-sm font-medium"
          >
            {showFilters ? 'Hide Filters' : 'Show Filters'}
          </button>
        </div>

        {/* Location Search */}
        <div className="relative mb-3">
          <MapPin className="absolute left-3 top-3 w-5 h-5 text-gray-400" />
          <input
            type="text"
            placeholder="Search by location..."
            value={searchLocation}
            onChange={(e) => setSearchLocation(e.target.value)}
            className="w-full pl-10 pr-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
        </div>

        {/* Sort Dropdown */}
        <div className="relative">
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="w-full px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          >
            <option value="newest">Newest First</option>
            <option value="cheapest">Cheapest First</option>
            <option value="expensive">Most Expensive First</option>
          </select>
        </div>

        {/* Advanced Filters */}
        {showFilters && (
          <div className="mt-4 pt-4 border-t space-y-4">
            {/* Price Range Slider */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Price Range: R{priceRange[0]} - R{priceRange[1]}
              </label>
              <div className="flex gap-3 items-center">
                <input
                  type="range"
                  min="0"
                  max="10000"
                  step="100"
                  value={priceRange[0]}
                  onChange={(e) => setPriceRange([parseInt(e.target.value), priceRange[1]])}
                  className="flex-1"
                />
                <input
                  type="range"
                  min="0"
                  max="10000"
                  step="100"
                  value={priceRange[1]}
                  onChange={(e) => setPriceRange([priceRange[0], parseInt(e.target.value)])}
                  className="flex-1"
                />
              </div>
              <div className="flex justify-between text-xs text-gray-500 mt-1">
                <span>R0</span>
                <span>R10,000</span>
              </div>
            </div>

            {/* Amenities Filter */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Amenities ({selectedAmenities.length} selected)
              </label>
              <div className="grid grid-cols-2 gap-2">
                {availableAmenities.map(amenity => (
                  <button
                    key={amenity}
                    onClick={() => toggleAmenity(amenity)}
                    className={`px-3 py-2 rounded-lg text-sm transition ${
                      selectedAmenities.includes(amenity)
                        ? 'bg-blue-600 text-white'
                        : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                    }`}
                  >
                    {amenity}
                  </button>
                ))}
              </div>
            </div>

            {/* Clear Filters */}
            {(selectedAmenities.length > 0 || priceRange[0] > 0 || priceRange[1] < 10000) && (
              <button
                onClick={() => {
                  setSelectedAmenities([]);
                  setPriceRange([0, 10000]);
                  setSearchLocation('');
                }}
                className="w-full bg-gray-200 hover:bg-gray-300 text-gray-700 py-2 rounded-lg transition text-sm"
              >
                Clear All Filters
              </button>
            )}
          </div>
        )}
      </div>

      {/* Results Count */}
      <div className="mb-4 text-sm text-gray-600">
        Found {listings.length} room{listings.length !== 1 ? 's' : ''}
      </div>

      {/* Listings */}
      <div className="space-y-4">
        {listings.length === 0 ? (
          <div className="bg-white rounded-lg shadow p-8 text-center text-gray-500">
            <Home className="w-16 h-16 mx-auto mb-4 text-gray-300" />
            <p>No rooms match your filters.</p>
            <button
              onClick={() => {
                setSelectedAmenities([]);
                setPriceRange([0, 10000]);
                setSearchLocation('');
              }}
              className="mt-4 text-blue-600 hover:text-blue-700 font-medium"
            >
              Clear filters
            </button>
          </div>
        ) : (
          listings.map(listing => (
            <ListingCard key={listing.id} listing={listing} onClick={() => onSelectListing(listing)} />
          ))
        )}
      </div>
    </div>
  );
}

