import React, { useMemo, useEffect, useState, useCallback, useRef } from 'react';
import Map, { Marker, Popup, Source, Layer, NavigationControl } from 'react-map-gl/maplibre';
import maplibregl from 'maplibre-gl';
import 'maplibre-gl/dist/maplibre-gl.css';

const DEFAULT_CENTER = { lat: -26.2041, lon: 28.0473 };
const MAP_STYLE_OPTIONS = (() => {
  const baseStyles = [
    { id: 'streets', label: 'Streets', icon: 'üó∫Ô∏è', url: 'https://basemaps.cartocdn.com/gl/positron-gl-style/style.json' },
    { id: 'dark', label: 'Dark', icon: 'üåô', url: 'https://basemaps.cartocdn.com/gl/dark-matter-gl-style/style.json' },
    { id: 'terrain', label: 'Terrain', icon: '‚õ∞Ô∏è', url: 'https://basemaps.cartocdn.com/gl/voyager-gl-style/style.json' }
  ];
  const maptilerKey = process.env.REACT_APP_MAPTILER_KEY;
  if (maptilerKey) {
    baseStyles.push({
      id: 'satellite',
      label: 'Satellite',
      icon: 'üõ∞Ô∏è',
      url: `https://api.maptiler.com/maps/hybrid/style.json?key=${maptilerKey}`
    });
  }
  return baseStyles;
})();
const MAP_STYLE_LOOKUP = MAP_STYLE_OPTIONS.reduce((acc, option) => {
  acc[option.id] = option;
  return acc;
}, {});

const locationCoordinates = {
  'soweto': [-26.2373, 27.8471],
  'johannesburg': [-26.2023, 28.0436],
  'pretoria': [-25.7461, 28.2333],
  'durban': [-29.8587, 31.0192],
  'cape town': [-33.9249, 18.4241],
  'sandton': [-26.1088, 28.0545],
  'midrand': [-25.9860, 28.1144],
  'benoni': [-26.1917, 28.3603],
  'boksburg': [-26.1972, 28.2328],
  'alberton': [-26.2745, 28.1114],
  'randburg': [-26.0936, 28.0065],
  'roodepoort': [-26.1625, 27.8725],
  'kempton park': [-26.1000, 28.2300],
  'germiston': [-26.2200, 28.1700],
  'springs': [-26.2500, 28.4400],
  'ivory park': [-25.9800, 28.1900],
  'tembisa': [-25.9956, 28.2269],
  'alexandra': [-26.1050, 28.0900],
  'diepsloot': [-25.9333, 28.0167],
  'centurion': [-25.8603, 28.1894],
};

const getCoordinatesForLocation = (location) => {
  if (!location) return [-26.2023, 28.0436];
  const normalized = location.toLowerCase().trim();
  for (const [key, coords] of Object.entries(locationCoordinates)) {
    if (normalized.includes(key)) {
      // Add slight random offset to prevent marker stacking
      const offset = () => (Math.random() - 0.5) * 0.008;
      return [coords[0] + offset(), coords[1] + offset()];
    }
  }
  return [-26.2023, 28.0436]; // Default to Johannesburg
};

// Calculate distance between two points
const getDistance = (lat1, lon1, lat2, lon2) => {
  const R = 6371; // Earth's radius in km
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
            Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
            Math.sin(dLon/2) * Math.sin(dLon/2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
  return R * c;
};
const createRadiusGeoJson = (center, radiusKm) => {
  if (!center || !radiusKm) return null;
  const steps = 64;
  const coords = [];
  const { lat, lon } = center;
  const latRadius = (radiusKm / 110.574);
  const lonRadius = radiusKm / (111.32 * Math.cos((lat * Math.PI) / 180));
  for (let i = 0; i <= steps; i += 1) {
    const angle = (i / steps) * (Math.PI * 2);
    const pointLat = lat + latRadius * Math.sin(angle);
    const pointLon = lon + lonRadius * Math.cos(angle);
    coords.push([pointLon, pointLat]);
  }
  return {
    type: 'Feature',
    geometry: {
      type: 'Polygon',
      coordinates: [coords]
    }
  };
};

const formatPrice = (price) => {
  if (typeof price !== 'number') return 'R0';
  return price >= 1000 ? `R${(price / 1000).toFixed(price % 1000 === 0 ? 0 : 1)}k` : `R${price}`;
};

function PriceMarker({ price, isSelected, hasWifi, isNew, onClick, onMouseEnter, onMouseLeave }) {
  const badges = [];
  if (hasWifi) badges.push('üì∂');
  if (isNew) badges.push('‚ú®');
  return (
    <div
      className={`price-marker-container ${isSelected ? 'selected' : ''}`}
      role="button"
      tabIndex={0}
      onClick={onClick}
      onKeyDown={(e) => { if (e.key === 'Enter') onClick?.(); }}
      onMouseEnter={onMouseEnter}
      onMouseLeave={onMouseLeave}
    >
      {badges.length > 0 && <div className="marker-badges">{badges.join('')}</div>}
      <div className="price-marker-bubble">
        <span className="price-text">{formatPrice(price)}</span>
      </div>
      <div className="price-marker-arrow" />
      <div className="price-marker-shadow" />
    </div>
  );
}

function Compass({ bearing = 0, onReset }) {
  return (
    <button
      className="map-compass"
      onClick={onReset}
      title="Reset orientation"
      style={{ transform: `rotate(${bearing.toFixed(1)}deg)` }}
      type="button"
    >
      <svg width="24" height="24" viewBox="0 0 24 24" fill="none">
        <path d="M12 2L8 10H16L12 2Z" fill="#ef4444" />
        <path d="M12 22L16 14H8L12 22Z" fill="#374151" />
        <circle cx="12" cy="12" r="2" fill="#374151" />
      </svg>
    </button>
  );
}

// Legend component
function MapLegend({ userLocation, nearbyRadius, priceRange }) {
  const [isOpen, setIsOpen] = useState(false);
  
  return (
    <div className={`map-legend ${isOpen ? 'open' : ''}`}>
      <button className="legend-toggle" onClick={() => setIsOpen(!isOpen)}>
        <span>‚ÑπÔ∏è</span>
        <span className="legend-toggle-text">{isOpen ? 'Hide' : 'Legend'}</span>
      </button>
      {isOpen && (
        <div className="legend-content">
          <div className="legend-item">
            <div className="legend-marker price-low"></div>
            <span>R0 - R2,000/mo</span>
          </div>
          <div className="legend-item">
            <div className="legend-marker price-mid"></div>
            <span>R2,000 - R4,000/mo</span>
          </div>
          <div className="legend-item">
            <div className="legend-marker price-high"></div>
            <span>R4,000+/mo</span>
          </div>
          {userLocation && (
            <div className="legend-item">
              <div className="legend-user-dot"></div>
              <span>Your location</span>
            </div>
          )}
          {nearbyRadius > 0 && (
            <div className="legend-item">
              <div className="legend-radius"></div>
              <span>{nearbyRadius}km radius</span>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

// Quick filters for map
function MapQuickFilters({ onFilterChange, activeFilter }) {
  const filters = [
    { id: 'all', label: 'All', icon: 'üè†' },
    { id: 'cheap', label: 'Under R2k', icon: 'üí∞' },
    { id: 'mid', label: 'R2k-R4k', icon: 'üè°' },
    { id: 'wifi', label: 'WiFi', icon: 'üì∂' },
  ];

  return (
    <div className="map-quick-filters">
      {filters.map(f => (
        <button
          key={f.id}
          className={`quick-filter-btn ${activeFilter === f.id ? 'active' : ''}`}
          onClick={() => onFilterChange(f.id)}
        >
          <span>{f.icon}</span>
          <span className="filter-label">{f.label}</span>
        </button>
      ))}
    </div>
  );
}

// Map style selector component
function MapStyleSelector({ currentStyle, onStyleChange, options }) {
  return (
    <div className="map-style-selector">
      {options.map(style => (
        <button
          key={style.id}
          onClick={() => onStyleChange(style.id)}
          className={`map-style-btn ${currentStyle === style.id ? 'active' : ''}`}
          title={style.label}
        >
          <span className="style-icon">{style.icon}</span>
          <span className="style-label">{style.label}</span>
        </button>
      ))}
    </div>
  );
}

function OverlayControls({
  userLocation,
  nearbyRadius,
  onRecenter,
  onRadiusChange,
  mapStyle,
  onMapStyleChange,
  listingsCount,
  zoomLevel,
  onSearchArea,
  onZoomIn,
  onZoomOut,
  wrapperRef,
  mapStyleOptions
}) {
  const [isExpanded, setIsExpanded] = useState(false);
  const [showSearch, setShowSearch] = useState(false);
  return (
    <>
      {/* Top-left: Listings count badge with stats */}
      <div className="map-listings-badge">
        <div className="badge-main">
          <span className="badge-icon">üè†</span>
          <span className="badge-count">{listingsCount}</span>
          <span className="badge-label">rooms</span>
        </div>
        {userLocation && nearbyRadius > 0 && (
          <div className="badge-subtitle">within {nearbyRadius}km</div>
        )}
      </div>

      {/* Top-right: Controls */}
      <div className="map-controls-container">
        {/* Search in map area */}
        <button
          onClick={() => setShowSearch(!showSearch)}
          className="map-control-btn search-btn"
          title="Search this area"
        >
          <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <circle cx="11" cy="11" r="8"/>
            <path d="m21 21-4.35-4.35"/>
          </svg>
        </button>

        {/* Map style toggle */}
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className={`map-control-btn map-style-toggle ${isExpanded ? 'active' : ''}`}
          title="Change map style"
        >
          <span>üó∫Ô∏è</span>
        </button>
        
        {isExpanded && (
          <MapStyleSelector 
            currentStyle={mapStyle} 
            onStyleChange={(style) => {
              onMapStyleChange(style);
              setIsExpanded(false);
            }}
            options={mapStyleOptions}
          />
        )}

        {/* Recenter button */}
        {userLocation && (
          <button
            type="button"
            onClick={onRecenter}
            className="map-control-btn recenter-btn"
            title="Center on my location"
          >
            <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
              <circle cx="12" cy="12" r="3"/>
              <path d="M12 2v4M12 18v4M2 12h4M18 12h4"/>
            </svg>
          </button>
        )}

        {/* Zoom controls */}
        <div className="zoom-controls">
          <button onClick={onZoomIn} className="zoom-btn zoom-in" title="Zoom in">+</button>
          <div className="zoom-level">{zoomLevel}</div>
          <button onClick={onZoomOut} className="zoom-btn zoom-out" title="Zoom out">‚àí</button>
        </div>

        {/* Fullscreen toggle */}
        <button
          type="button"
          onClick={() => {
            const container = wrapperRef?.current;
            if (!container) return;
            if (document.fullscreenElement) {
              document.exitFullscreen();
            } else {
              container.requestFullscreen?.();
            }
          }}
          className="map-control-btn fullscreen-btn"
          title="Toggle fullscreen"
        >
          <svg width="18" height="18" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
            <path d="M8 3H5a2 2 0 0 0-2 2v3M21 8V5a2 2 0 0 0-2-2h-3M3 16v3a2 2 0 0 0 2 2h3M16 21h3a2 2 0 0 0 2-2v-3"/>
          </svg>
        </button>
      </div>

      {/* Search area notification */}
      {showSearch && (
        <div className="map-search-area">
          <button 
            className="search-area-btn"
            onClick={() => {
              // Trigger search in visible area
              setShowSearch(false);
              onSearchArea?.();
            }}
          >
            üîç Search this area
          </button>
        </div>
      )}

      {/* Bottom: Radius slider - enhanced */}
      {userLocation && (
        <div className="map-radius-control">
          <div className="radius-header">
            <span className="radius-icon">üìç</span>
            <span className="radius-label">Search radius</span>
            <span className="radius-value-inline">{nearbyRadius} km</span>
          </div>
          <div className="radius-slider-container">
            <span className="radius-min">1</span>
            <input
              type="range"
              min={1}
              max={25}
              step={1}
              value={nearbyRadius}
              onChange={(e) => onRadiusChange && onRadiusChange(parseInt(e.target.value))}
              className="radius-slider"
            />
            <span className="radius-max">25</span>
          </div>
          <div className="radius-presets">
            {[1, 3, 5, 10, 15, 25].map(r => (
              <button
                key={r}
                onClick={() => onRadiusChange && onRadiusChange(r)}
                className={`radius-preset-btn ${nearbyRadius === r ? 'active' : ''}`}
              >
                {r}km
              </button>
            ))}
          </div>
        </div>
      )}
    </>
  );
}

export default function MapView({ listings = [], onMarkerClick = () => {}, userLocation = null, nearbyRadius = 0, fullHeight = false, onRadiusChange, onRecenter }) {
  const [mapStyle, setMapStyle] = useState(MAP_STYLE_OPTIONS[0]?.id || 'streets');
  const [selectedMarkerId, setSelectedMarkerId] = useState(null);
  const [hoveredMarkerId, setHoveredMarkerId] = useState(null);
  const [zoomLevel, setZoomLevel] = useState(12);
  const [activeFilter, setActiveFilter] = useState('all');
  const [shouldFitBounds, setShouldFitBounds] = useState(true);
  const [viewState, setViewState] = useState({
    latitude: DEFAULT_CENTER.lat,
    longitude: DEFAULT_CENTER.lon,
    zoom: 11,
    pitch: 0,
    bearing: 0
  });
  const mapRef = useRef(null);
  const wrapperRef = useRef(null);

  const markers = useMemo(() => {
    return listings
      .filter(l => {
        // Apply active filter
        if (activeFilter === 'cheap') return l.price < 2000;
        if (activeFilter === 'mid') return l.price >= 2000 && l.price <= 4000;
        if (activeFilter === 'wifi') return l.wifi || l.amenities?.includes('WiFi');
        return true;
      })
      .map(l => {
        let lat = l.latitude ?? l.lat ?? (l.coords && l.coords[0]);
        let lng = l.longitude ?? l.lng ?? (l.coords && l.coords[1]);
        
        // Fallback to location-based coordinates if no explicit coords
        if (typeof lat !== 'number' || typeof lng !== 'number') {
          [lat, lng] = getCoordinatesForLocation(l.location);
        }
        
        // Check if listing is new (within 7 days)
        const isNew = l.createdAt && (Date.now() - new Date(l.createdAt).getTime()) < 7 * 24 * 60 * 60 * 1000;
        
        // Calculate distance from user if available
        let distance = null;
        if (userLocation && typeof userLocation.lat === 'number') {
          distance = getDistance(userLocation.lat, userLocation.lon, lat, lng);
        }
        
        return { 
          id: l.id, 
          title: l.title, 
          location: l.location, 
          price: l.price, 
          lat, 
          lng, 
          raw: l, 
          photos: l.photos,
          hasWifi: l.wifi || l.amenities?.includes('WiFi'),
          isNew,
          distance
        };
      });
  }, [listings, activeFilter, userLocation]);

  const bounds = markers.length > 0 ? markers.map(m => [m.lat, m.lng]) : null;
  const allBounds = useMemo(() => {
    const b = bounds ? [...bounds] : [];
    if (userLocation && typeof userLocation.lat === 'number' && typeof userLocation.lon === 'number') {
      b.push([userLocation.lat, userLocation.lon]);
    }
    return b.length > 0 ? b : null;
  }, [bounds, userLocation]);

  useEffect(() => {
    setShouldFitBounds(true);
  }, [listings, userLocation]);

  useEffect(() => {
    if (!shouldFitBounds || !allBounds || allBounds.length === 0) return;
    const mapInstance = mapRef.current?.getMap();
    if (!mapInstance) return;
    const boundsHelper = new maplibregl.LngLatBounds();
    allBounds.forEach(([lat, lng]) => boundsHelper.extend([lng, lat]));
    if (!boundsHelper.isEmpty()) {
      mapInstance.fitBounds(boundsHelper, { padding: 80, maxZoom: 14, duration: 800 });
    }
  }, [allBounds, shouldFitBounds]);

  const radiusGeoJson = useMemo(() => createRadiusGeoJson(userLocation, nearbyRadius), [userLocation, nearbyRadius]);
  const selectedMarker = useMemo(() => markers.find(m => m.id === selectedMarkerId) || null, [markers, selectedMarkerId]);
  const mapHeight = fullHeight ? 'calc(100vh - 180px)' : '320px';

  const handleMarkerClick = useCallback((marker) => {
    setSelectedMarkerId(marker.id);
    onMarkerClick(marker.raw);
  }, [onMarkerClick]);

  const handleRecenter = useCallback(() => {
    if (!userLocation) return;
    const mapInstance = mapRef.current?.getMap();
    if (mapInstance) {
      mapInstance.flyTo({ center: [userLocation.lon, userLocation.lat], zoom: 14, duration: 800, essential: true });
    }
    onRecenter?.();
  }, [onRecenter, userLocation]);

  const handleZoomIn = useCallback(() => {
    mapRef.current?.getMap()?.zoomIn();
  }, []);

  const handleZoomOut = useCallback(() => {
    mapRef.current?.getMap()?.zoomOut();
  }, []);

  const handleCompassReset = useCallback(() => {
    const mapInstance = mapRef.current?.getMap();
    if (mapInstance) {
      mapInstance.easeTo({ bearing: 0, pitch: 0, duration: 600 });
    } else {
      setViewState((prev) => ({ ...prev, bearing: 0, pitch: 0 }));
    }
  }, []);

  const onMove = useCallback((evt) => {
    setViewState(evt.viewState);
    setZoomLevel(Number(evt.viewState.zoom?.toFixed(1) || 0));
    setShouldFitBounds(false);
  }, []);

  const mapStyleUrl = MAP_STYLE_LOOKUP[mapStyle]?.url || MAP_STYLE_LOOKUP.streets?.url;

  return (
    <div ref={wrapperRef} className="map-container-wrapper" style={{ height: mapHeight }}>
      {markers.length === 0 ? (
        <div className="map-empty-state">
          <div className="empty-state-icon">üó∫Ô∏è</div>
          <h3 className="empty-state-title">No listings on the map</h3>
          <p className="empty-state-text">
            {listings.length === 0 
              ? "No listings available in this area" 
              : "Listings don't have location data yet"}
          </p>
          {listings.length > 0 && (
            <div className="empty-state-list">
              {listings.slice(0, 3).map(l => (
                <button 
                  key={l.id} 
                  onClick={() => onMarkerClick(l)} 
                  className="empty-state-listing"
                >
                  <span className="listing-title">{l.title}</span>
                  <span className="listing-price">R{Number(l.price).toLocaleString()}</span>
                </button>
              ))}
              {listings.length > 3 && (
                <p className="empty-state-more">+{listings.length - 3} more listings</p>
              )}
            </div>
          )}
        </div>
      ) : (
        <div className="map-container">
          <Map
            ref={mapRef}
            mapLib={maplibregl}
            mapStyle={mapStyleUrl}
            {...viewState}
            onMove={onMove}
            attributionControl={false}
            style={{ width: '100%', height: '100%', borderRadius: '16px' }}
          >
            <NavigationControl position="bottom-right" showCompass={false} />
          <OverlayControls 
            userLocation={userLocation} 
            nearbyRadius={nearbyRadius} 
            onRecenter={() => { setShouldFitBounds(false); handleRecenter(); }} 
            onRadiusChange={onRadiusChange}
            mapStyle={mapStyle}
            onMapStyleChange={(style) => {
              setMapStyle(style);
              setShouldFitBounds(false);
            }}
            listingsCount={markers.length}
            zoomLevel={zoomLevel}
            onSearchArea={() => setShouldFitBounds(false)}
            onZoomIn={handleZoomIn}
            onZoomOut={handleZoomOut}
            wrapperRef={wrapperRef}
            mapStyleOptions={MAP_STYLE_OPTIONS}
          />
            <Compass bearing={viewState.bearing} onReset={handleCompassReset} />
            <MapLegend userLocation={userLocation} nearbyRadius={nearbyRadius} />
            <MapQuickFilters activeFilter={activeFilter} onFilterChange={setActiveFilter} />

            {userLocation && typeof userLocation.lat === 'number' && typeof userLocation.lon === 'number' && (
              <>
                <Marker longitude={userLocation.lon} latitude={userLocation.lat} anchor="center">
                  <div className="user-location-container">
                    <div className="user-location-pulse" />
                    <div className="user-location-dot" />
                  </div>
                </Marker>
                {radiusGeoJson && (
                  <Source id="radius" type="geojson" data={radiusGeoJson}>
                    <Layer
                      id="radius-fill"
                      type="fill"
                      paint={{ 'fill-color': '#0d9488', 'fill-opacity': 0.08 }}
                    />
                    <Layer
                      id="radius-outline"
                      type="line"
                      paint={{ 'line-color': '#0d9488', 'line-width': 2, 'line-dasharray': [2, 2] }}
                    />
                  </Source>
                )}
              </>
            )}

            {markers.map((m) => (
              <Marker key={m.id} longitude={m.lng} latitude={m.lat} anchor="bottom">
                <PriceMarker
                  price={m.price}
                  hasWifi={m.hasWifi}
                  isNew={m.isNew}
                  isSelected={selectedMarkerId === m.id || hoveredMarkerId === m.id}
                  onClick={() => handleMarkerClick(m)}
                  onMouseEnter={() => setHoveredMarkerId(m.id)}
                  onMouseLeave={() => setHoveredMarkerId(null)}
                />
              </Marker>
            ))}

            {selectedMarker && (
              <Popup
                longitude={selectedMarker.lng}
                latitude={selectedMarker.lat}
                anchor="bottom"
                closeButton={false}
                maxWidth="320px"
                className="listing-popup"
                onClose={() => setSelectedMarkerId(null)}
              >
                <div className="popup-listing-card">
                  {selectedMarker.photos && selectedMarker.photos[0] && (
                    <div className="popup-image">
                      <img src={selectedMarker.photos[0]} alt={selectedMarker.title} />
                      {selectedMarker.isNew && <span className="popup-badge new">New</span>}
                    </div>
                  )}
                  <div className="popup-details">
                    <h4 className="popup-title">{selectedMarker.title}</h4>
                    <p className="popup-location">
                      <span className="location-icon">üìç</span>
                      {selectedMarker.location}
                      {selectedMarker.distance !== null && (
                        <span className="distance-badge">{selectedMarker.distance.toFixed(1)}km</span>
                      )}
                    </p>
                    <div className="popup-amenities">
                      {selectedMarker.hasWifi && <span className="amenity-badge">üì∂ WiFi</span>}
                    </div>
                    <div className="popup-footer">
                      <span className="popup-price">R{Number(selectedMarker.price).toLocaleString()}<small>/mo</small></span>
                      <button className="popup-view-btn" onClick={() => handleMarkerClick(selectedMarker)}>
                        View Details ‚Üí
                      </button>
                    </div>
                  </div>
                </div>
              </Popup>
            )}
          </Map>
        </div>
      )}
    </div>
  );
}
