function AddListingView({ onSubmit, onCancel }) {
  const [formData, setFormData] = useState({
    title: '',
    price: '',
    location: '',
    description: '',
    photos: [],
    status: 'available',
    availableDate: new Date().toISOString().split('T')[0],
    amenities: []
  });

  const availableAmenities = [
    'WiFi', 'Parking', 'Kitchen', 'Laundry', 'Air Conditioning', 
    'Heating', 'TV', 'Furnished', 'Pet Friendly', 'Garden'
  ];

  const toggleAmenity = (amenity) => {
    if (formData.amenities.includes(amenity)) {
      setFormData({ ...formData, amenities: formData.amenities.filter(a => a !== amenity) });
    } else {
      setFormData({ ...formData, amenities: [...formData.amenities, amenity] });
    }
  };

  const handlePhotoUpload = (e) => {
    const files = Array.from(e.target.files || []);
    if (files.length === 0) return;

    const readers = files.map(file => new Promise((resolve) => {
      const reader = new FileReader();
      reader.onloadend = () => resolve(reader.result);
      reader.readAsDataURL(file);
    }));

    Promise.all(readers).then(images => {
      const combined = [...formData.photos, ...images].slice(0, 5); // cap at 5
      setFormData({ ...formData, photos: combined });
    });
  };

  const removePhoto = (index) => {
    const newPhotos = formData.photos.filter((_, i) => i !== index);
    setFormData({ ...formData, photos: newPhotos });
  };

  const handleSubmit = () => {
    if (formData.title && formData.price && formData.location) {
      onSubmit(formData);
    }
  };

  return (
    <div className="p-4 bg-gradient-to-br from-slate-900 to-purple-900 min-h-screen">
      <div className="bg-gradient-to-br from-slate-800 to-slate-700 rounded-xl shadow-lg p-6 border border-purple-500/30">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 to-pink-400">Post a Room</h2>
          <button onClick={onCancel} className="text-gray-400 hover:text-gray-300 transition">
            <X className="w-6 h-6" />
          </button>
        </div>

        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-cyan-300 mb-1">Room Title</label>
            <input
              type="text"
              required
              value={formData.title}
              onChange={(e) => setFormData({ ...formData, title: e.target.value })}
              placeholder="e.g., Cozy backroom with bathroom"
              className="w-full px-4 py-2 border border-purple-500/50 bg-slate-700 text-white rounded-lg focus:ring-2 focus:ring-cyan-400 focus:border-transparent placeholder-gray-400"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-cyan-300 mb-1">Monthly Price (R)</label>
            <input
              type="number"
              required
              value={formData.price}
              onChange={(e) => setFormData({ ...formData, price: e.target.value })}
              placeholder="e.g., 2500"
              className="w-full px-4 py-2 border border-purple-500/50 bg-slate-700 text-white rounded-lg focus:ring-2 focus:ring-cyan-400 focus:border-transparent placeholder-gray-400"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-cyan-300 mb-1">Location</label>
            <input
              type="text"
              required
              value={formData.location}
              onChange={(e) => setFormData({ ...formData, location: e.target.value })}
              placeholder="e.g., Soweto, Johannesburg"
              className="w-full px-4 py-2 border border-purple-500/50 bg-slate-700 text-white rounded-lg focus:ring-2 focus:ring-cyan-400 focus:border-transparent placeholder-gray-400"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-cyan-300 mb-1">Description</label>
            <textarea
              value={formData.description}
              onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              placeholder="Describe the room, amenities, rules..."
              rows="4"
              className="w-full px-4 py-2 border border-purple-500/50 bg-slate-700 text-white rounded-lg focus:ring-2 focus:ring-cyan-400 focus:border-transparent placeholder-gray-400"
            />
          </div>

          <div>
  <label className="block text-sm font-medium text-cyan-300 mb-1">Status</label>
  <select
    value={formData.status}
    onChange={(e) => setFormData({ ...formData, status: e.target.value })}
    className="w-full px-4 py-2 border border-purple-500/50 bg-slate-700 text-white rounded-lg focus:ring-2 focus:ring-cyan-400 focus:border-transparent"
  >
    <option value="available">Available</option>
    <option value="rented">Rented</option>
  </select>
</div>

<div>
  <label className="block text-sm font-medium text-cyan-300 mb-1">Available Date</label>
  <input
    type="date"
    value={formData.availableDate}
    onChange={(e) => setFormData({ ...formData, availableDate: e.target.value })}
    className="w-full px-4 py-2 border border-purple-500/50 bg-slate-700 text-white rounded-lg focus:ring-2 focus:ring-cyan-400 focus:border-transparent"
  />
</div>

          <div>
            <label className="block text-sm font-medium text-cyan-300 mb-2">Amenities</label>
            <div className="grid grid-cols-2 gap-2">
              {availableAmenities.map(amenity => (
                <button
                  key={amenity}
                  type="button"
                  onClick={() => toggleAmenity(amenity)}
                  className={`px-3 py-2 rounded-lg text-sm transition ${
                    formData.amenities.includes(amenity)
                      ? 'bg-gradient-to-r from-cyan-500 to-blue-600 text-white shadow-lg shadow-cyan-500/50'
                      : 'bg-slate-600/50 text-gray-300 hover:bg-purple-600/50 border border-purple-500/30'
                  }`}
                >
                  {amenity}
                </button>
              ))}
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-cyan-300 mb-1">Photos (Max 5)</label>
            <input
              type="file"
              accept="image/*"
              multiple
              onChange={handlePhotoUpload}
              disabled={formData.photos.length >= 5}
              className="w-full px-4 py-2 border border-purple-500/50 bg-slate-700 text-white rounded-lg focus:ring-2 focus:ring-cyan-400 focus:border-transparent"
            />
            {formData.photos.length > 0 && (
              <div className="mt-3 grid grid-cols-3 gap-2">
                {formData.photos.map((photo, index) => (
                  <div key={index} className="relative border border-cyan-500/30 rounded-lg overflow-hidden">
                    <img src={photo} alt={`Preview ${index + 1}`} className="w-full h-32 object-cover rounded-lg" />
                    <button
                      type="button"
                      onClick={() => removePhoto(index)}
                      className="absolute top-1 right-1 bg-red-600 text-white rounded-full w-6 h-6 flex items-center justify-center hover:bg-red-700 shadow-lg"
                    >
                      <X className="w-4 h-4" />
                    </button>
                  </div>
                ))}
              </div>
            )}
            {formData.photos.length >= 5 && (
              <p className="text-sm text-orange-400 mt-2">Maximum 5 photos reached</p>
            )}
          </div>

          <button
            onClick={handleSubmit}
            className="w-full bg-gradient-to-r from-cyan-500 to-blue-600 hover:shadow-lg hover:shadow-cyan-500/50 text-white font-semibold py-3 rounded-lg transition transform hover:scale-105 mt-6"
          >
            Post Room
          </button>
        </div>
      </div>
    </div>
  );
}

