          </div>

          {userType === 'renter' && landlord && (
            <div className="bg-blue-50 rounded-lg p-4 mb-4">
              <div className="flex items-center justify-between mb-3">
                <h4 className="font-semibold text-gray-700">Landlord Information</h4>
                <button
                  onClick={() => setShowContact(!showContact)}
                  className="text-blue-600 hover:text-blue-700 text-sm font-medium"
                >
                  {showContact ? 'Hide' : 'Show'} Contact
                </button>
              </div>

              <div className="flex items-center mb-3">
                {landlord.photo ? (
                  <img src={landlord.photo} alt={landlord.name} className="w-12 h-12 rounded-full object-cover mr-3" />
                ) : (
                  <div className="w-12 h-12 rounded-full bg-gray-300 flex items-center justify-center mr-3">
                    <User className="w-6 h-6 text-gray-600" />
                  </div>
                )}
                <div>
                  <p className="font-semibold text-gray-800">{landlord.name}</p>
                  <p className="text-xs text-gray-500">Property Owner</p>
                </div>
              </div>

              {showContact && (
                <div className="space-y-2 pt-3 border-t border-blue-100">
                  <div className="flex items-center text-sm">
                    <Phone className="w-4 h-4 mr-2 text-blue-600" />
                    <a href={`tel:${landlord.phone}`} className="text-blue-600 hover:underline">
                      {landlord.phone}
                    </a>
                  </div>
                  <div className="flex items-center text-sm">
                    <Mail className="w-4 h-4 mr-2 text-blue-600" />
                    <a href={`mailto:${landlord.email}`} className="text-blue-600 hover:underline">
                      {landlord.email}
                    </a>
                  </div>
                </div>
              )}
            </div>
          )}

          {userType === 'renter' && (
            <div className="border-t pt-4">
              <h4 className="font-semibold mb-3 text-gray-700">Send a Message</h4>
              <div className="flex gap-2">
                <input
                  type="text"
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  placeholder="Type your message..."
                  className="flex-1 px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
                <button
                  onClick={handleSend}
                  className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg transition flex items-center"
                >
                  <Send className="w-5 h-5" />
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

function ConversationModal({ conversation, listing, onClose, onSendMessage }) {
  const [message, setMessage] = useState('');

  const handleSend = () => {
    if (message.trim()) {
      onSendMessage(listing.id, message);
      setMessage('');
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] flex flex-col">
        <div className="bg-white border-b p-4 flex items-center">
          <button onClick={onClose} className="mr-3 text-gray-500 hover:text-gray-700">
            <ArrowLeft className="w-6 h-6" />
          </button>
          <div>
            <h2 className="font-bold text-gray-800">{listing?.title}</h2>
            <p className="text-sm text-gray-600">{listing?.location}</p>
          </div>
        </div>

        <div className="flex-1 overflow-y-auto p-4 space-y-3">
          {conversation.messages.map(msg => (
            <div
              key={msg.id}
              className={`flex ${msg.sender === 'landlord' ? 'justify-start' : 'justify-end'}`}
            >
              <div
                className={`max-w-xs px-4 py-2 rounded-lg ${
                  msg.sender === 'landlord'
                    ? 'bg-gray-200 text-gray-800'
                    : 'bg-blue-600 text-white'
                }`}
              >
                <p>{msg.text}</p>
                <span className="text-xs opacity-75">
                  {new Date(msg.timestamp).toLocaleTimeString()}
                </span>
              </div>
            </div>
          ))}
        </div>

        <div className="border-t p-4 flex gap-2">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
            placeholder="Type a message..."
            className="flex-1 px-4 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
          <button
            onClick={handleSend}
            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-2 rounded-lg transition"
          >
            <Send className="w-5 h-5" />
          </button>
        </div>
      </div>
    </div>
  );
}

function PhotoGallery({ photos, currentIndex, onClose, onNavigate }) {
  return (
    <div className="fixed inset-0 bg-black z-50 flex flex-col">
      <div className="flex justify-between items-center p-4 text-white">
        <span className="text-sm">{currentIndex + 1} / {photos.length}</span>
        <button onClick={onClose} className="hover:bg-gray-800 p-2 rounded-full">
          <X className="w-6 h-6" />
        </button>
      </div>

      <div className="flex-1 flex items-center justify-center relative">
        {currentIndex > 0 && (
          <button
            onClick={() => onNavigate('prev')}
            className="absolute left-4 bg-black bg-opacity-50 text-white p-3 rounded-full hover:bg-opacity-70 z-10"
          >
            <ArrowLeft className="w-6 h-6" />
          </button>
        )}

        <img
          src={photos[currentIndex]}
          alt=""
          className="max-w-full max-h-full object-contain"
        />

        {currentIndex < photos.length - 1 && (
          <button
            onClick={() => onNavigate('next')}
            className="absolute right-4 bg-black bg-opacity-50 text-white p-3 rounded-full hover:bg-opacity-70 z-10"
          >
            <ArrowLeft className="w-6 h-6 transform rotate-180" />
          </button>
        )}
      </div>

      <div className="p-4 flex gap-2 overflow-x-auto">
        {photos.map((photo, index) => (
          <img
            key={index}
            src={photo}
            alt={`Thumbnail ${index + 1}`}
            onClick={() => onNavigate('goto', index)}
            className={`w-16 h-16 object-cover rounded cursor-pointer ${
              index === currentIndex ? 'ring-2 ring-blue-500' : 'opacity-60'
            }`}
          />
        ))}
      </div>
    </div>
  );
}

function MapView({ listings, onSelectListing }) {
  const [selectedPin, setSelectedPin] = useState(null);

  const getLocationCoordinates = (location) => {
    const areas = {
      'soweto': { x: 30, y: 60 },
      'sandton': { x: 60, y: 30 },
      'rosebank': { x: 55, y: 35 },
      'johannesburg': { x: 50, y: 50 },
      'midrand': { x: 45, y: 25 },
      'randburg': { x: 40, y: 35 },
      'roodepoort': { x: 25, y: 45 },
      'kempton park': { x: 70, y: 40 },
      'pretoria': { x: 50, y: 10 },
      'centurion': { x: 48, y: 20 },
    };

    const locationLower = location.toLowerCase();
    for (const [area, coords] of Object.entries(areas)) {
      if (locationLower.includes(area)) {
        return {
          x: coords.x + (Math.random() - 0.5) * 10,
          y: coords.y + (Math.random() - 0.5) * 10
        };
      }
    }

    return {
      x: 20 + Math.random() * 60,
      y: 20 + Math.random() * 60
    };
  };

  const listingsWithCoords = listings.map(listing => ({
    ...listing,
    coords: getLocationCoordinates(listing.location)
  }));

  return (
    <div className="bg-white rounded-lg shadow overflow-hidden">
      <div className="relative bg-gradient-to-br from-green-100 via-blue-50 to-green-100 h-[500px]">
        <svg className="absolute inset-0 w-full h-full opacity-20">
          <defs>
            <pattern id="grid" width="40" height="40" patternUnits="userSpaceOnUse">
              <path d="M 40 0 L 0 0 0 40" fill="none" stroke="gray" strokeWidth="0.5"/>
            </pattern>
          </defs>
          <rect width="100%" height="100%" fill="url(#grid)" />
        </svg>

        <svg className="absolute inset-0 w-full h-full">
          <line x1="0%" y1="30%" x2="100%" y2="30%" stroke="#cbd5e0" strokeWidth="3" />
          <line x1="0%" y1="60%" x2="100%" y2="60%" stroke="#cbd5e0" strokeWidth="3" />
          <line x1="30%" y1="0%" x2="30%" y2="100%" stroke="#cbd5e0" strokeWidth="3" />
          <line x1="70%" y1="0%" x2="70%" y2="100%" stroke="#cbd5e0" strokeWidth="3" />
        </svg>

        {listingsWithCoords.map((listing) => (
          <div
            key={listing.id}
            onClick={() => setSelectedPin(selectedPin === listing.id ? null : listing.id)}
            style={{
              position: 'absolute',
              left: `${listing.coords.x}%`,
              top: `${listing.coords.y}%`,
              transform: 'translate(-50%, -100%)',
            }}
            className="cursor-pointer transition-transform hover:scale-110"
          >
            <div className="relative">
              <MapPin 
                className={`w-8 h-8 ${
                  selectedPin === listing.id 
                    ? 'text-red-600' 
                    : listing.status === 'available' 
                      ? 'text-blue-600' 
                      : 'text-gray-400'
                }`}
                fill="currentColor"
              />
              <div className={`absolute -top-8 left-1/2 transform -translate-x-1/2 whitespace-nowrap px-2 py-1 rounded text-xs font-bold shadow-lg ${
                selectedPin === listing.id 
                  ? 'bg-red-600 text-white' 
                  : 'bg-white text-gray-800'
              }`}>
                R{listing.price}
              </div>

              {selectedPin === listing.id && (
                <div 
                  className="absolute left-1/2 transform -translate-x-1/2 bottom-full mb-2 w-64 bg-white rounded-lg shadow-xl p-3 z-10"
                  onClick={(e) => e.stopPropagation()}
                >
                  {listing.photos && listing.photos.length > 0 && (
                    <img 
                      src={listing.photos[0]} 
                      alt={listing.title} 
                      className="w-full h-32 object-cover rounded-lg mb-2"
                    />
                  )}
                  <h4 className="font-bold text-sm mb-1">{listing.title}</h4>
                  <p className="text-xs text-gray-600 mb-2">{listing.location}</p>
                  <div className="flex items-center justify-between">
                    <span className="text-blue-600 font-bold">R{listing.price}/mo</span>
                    <button
                      onClick={() => onSelectListing(listing)}
                      className="bg-blue-600 hover:bg-blue-700 text-white text-xs px-3 py-1 rounded"
                    >
                      View Details
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        ))}

        <div className="absolute bottom-4 left-4 bg-white rounded-lg shadow-lg p-3">
          <h4 className="font-semibold text-sm mb-2">Legend</h4>
          <div className="space-y-1 text-xs">
            <div className="flex items-center gap-2">
              <MapPin className="w-4 h-4 text-blue-600" fill="currentColor" />
              <span>Available</span>
            </div>
            <div className="flex items-center gap-2">
              <MapPin className="w-4 h-4 text-gray-400" fill="currentColor" />
              <span>Rented</span>
            </div>
            <div className="flex items-center gap-2">
              <MapPin className="w-4 h-4 text-red-600" fill="currentColor" />
              <span>Selected</span>
            </div>
          </div>
        </div>

        <div className="absolute top-4 left-4 bg-blue-600 text-white rounded-lg shadow-lg px-4 py-2">
          <div className="flex items-center gap-2">
            <Map className="w-5 h-5" />
            <span className="font-semibold">{listings.length} rooms on map</span>
          </div>
        </div>
      </div>

      <div className="p-4 max-h-48 overflow-y-auto">
        <h4 className="font-semibold mb-2 text-sm text-gray-700">Nearby Rooms</h4>
        <div className="space-y-2">
          {listings.slice(0, 5).map(listing => (
            <div
              key={listing.id}
              onClick={() => onSelectListing(listing)}
              className="flex items-center gap-3 p-2 hover:bg-gray-50 rounded-lg cursor-pointer transition"
            >
              {listing.photos && listing.photos.length > 0 && (
                <img 
                  src={listing.photos[0]} 
                  alt={listing.title}
                  className="w-12 h-12 object-cover rounded"
                />
              )}
              <div className="flex-1 min-w-0">
                <h5 className="font-semibold text-sm truncate">{listing.title}</h5>
                <p className="text-xs text-gray-600 truncate">{listing.location}</p>
              </div>
              <span className="text-blue-600 font-bold text-sm">R{listing.price}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

